n  // Automatically switch back to the original page\\n\\n  Ensure.that(Page.current().url().href, equals(\'https://serenity-js.org\'),\\n)\\n```"},{"kind":"text","text":"\\n\\n## Learn more\\n\\n- "},{"kind":"inline-tag","tag":"@apilink","text":"BrowseTheWeb"},{"kind":"text","text":"\\n- "},{"kind":"inline-tag","tag":"@apilink","text":"PageElement"},{"kind":"text","text":"\\n- "},{"kind":"inline-tag","tag":"@apilink","text":"Optional"},{"kind":"text","text":"\\n- "},{"kind":"inline-tag","tag":"@apilink","text":"Switchable"}]},"children":[{"id":7019,"name":"current","kind":2048,"kindString":"Method","flags":{"isStatic":true},"sources":[{"fileName":"packages/web/src/screenplay/models/Page.ts","line":138,"character":11,"url":"https://github.com/serenity-js/serenity-js/blob/7537aaf/packages/web/src/screenplay/models/Page.ts#L138"}],"signatures":[{"id":7020,"name":"current","kind":4096,"kindString":"Call signature","flags":{},"comment":{"summary":[{"kind":"text","text":"Creates a "},{"kind":"inline-tag","tag":"@apilink","text":"QuestionAdapter"},{"kind":"text","text":" representing the currently active "},{"kind":"inline-tag","tag":"@apilink","text":"Page"},{"kind":"text","text":"."}]},"type":{"type":"reference","typeArguments":[{"type":"reference","id":7018,"typeArguments":[{"type":"intrinsic","name":"any"}],"name":"Page"}],"name":"QuestionAdapter"}}]},{"id":7021,"name":"whichName","kind":2048,"kindString":"Method","flags":{"isStatic":true},"sources":[{"fileName":"packages/web/src/screenplay/models/Page.ts","line":162,"character":11,"url":"https://github.com/serenity-js/serenity-js/blob/7537aaf/packages/web/src/screenplay/models/Page.ts#L162"}],"signatures":[{"id":7022,"name":"whichName","kind":4096,"kindString":"Call signature","flags":{},"comment":{"summary":[{"kind":"text","text":"Creates a "},{"kind":"inline-tag","tag":"@apilink","text":"QuestionAdapter"},{"kind":"text","text":" that resolves to a "},{"kind":"inline-tag","tag":"@apilink","text":"Page"},{"kind":"tex