            });

            /** @test {not} */
            /** @test {isLessThan} */
            it('contributes to a human-readable description', () => {
                expect(Ensure.that(1, not(isLessThan(2))).toString())
                    .to.equal(`#actor ensures that 1 does not have value that's less than 2`);
