export * from './abilities';
export * from './interactions';
export * from './questions';

import { Answerable, AnswersQuestions, CollectsArtifacts, Interaction, UsesAbilities } from '@serenity-js/core';

import { ManageALocalServer } from '../abilities';

/**
 * @desc
 *  Starts local server so that a test can interact with it.
 */
export class StartLocalServer {

    /**
     * @desc
     *  Starts local test server on a random available ports.
     *
     * @see {@link LocalServer.url}
     *
     * @returns {@serenity-js/core/lib/screenplay~Interaction}
     */
    static onRandomPort(): Interaction {
        return new StartLocalServerOnRandomPort();
    }

    /**
     * @desc
     *  Instructs the {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  to start a local test server on one of the preferred ports.
     *
     *  Please note: this method is kept for backwards compatibility. However, its behaviour has changed
     *  and is currently identical to calling `StartLocalServer.onPort` with the first of `preferredPorts`
     *  passed as an argument.
     *
     * @param {@serenity-js/core/lib/screenplay~Answerable<number[]>} preferredPorts
     *  A list of preferred ports. Please note that only the first one will be used!
     *
     * @see {@link LocalServer.url}
     * @see {@link StartLocalServer.onPort}
     * @see {@link StartLocalServer.onRandomPortBetween}
     *
     * @deprecated Use `StartLocalServer.onPort` or `StartLocalServer.onRandomPortBetween`
     *
     * @returns {@serenity-js/core/lib/screenplay~Interaction}
     */
    static onOneOfThePreferredPorts(preferredPorts: Answerable<number[]>): Interaction {
        return new StartLocalServerOnFirstOf(preferredPorts);
    }

    /**
     * @desc
     *  Instructs the {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  to start a local test server on a `preferredPort`, or a random one if that's not available.
     *
     * @param {@serenity-js/core/lib/screenplay~Answerable<number>} preferredPort
     *  preferred port
     *
     * @see {@link LocalServer.url}
     * @see {@link @serenity-js/core/lib/screenplay~Answerable}
     *
     * @returns {@serenity-js/core/lib/screenplay~Interaction}
     */
    static onPort(preferredPort: Answerable<number>): Interaction {
        return new StartLocalServerOnPort(preferredPort);
    }

    /**
     * @desc
     *  Instructs the {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  to start a local test server on a random port between `lowestPort` and `highestPort`.
     *
     * @param {@serenity-js/core/lib/screenplay~Answerable<number>} lowestPort
     *  Lower bound of the preferred port range
     *
     * @param {@serenity-js/core/lib/screenplay~Answerable<number>} highestPort
     *  Upper bound of the preferred port range
     *
     * @see {@link LocalServer.url}
     *
     * @returns {@serenity-js/core/lib/screenplay~Interaction}
     */
    static onRandomPortBetween(lowestPort: Answerable<number>, highestPort: Answerable<number>): Interaction {
        return new StartLocalServerOnRandomPortBetween(lowestPort, highestPort);
    }
}

/**
 * @package
 */
class StartLocalServerOnRandomPort extends Interaction {

    performAs(actor: UsesAbilities & CollectsArtifacts & AnswersQuestions): Promise<void> {
        return ManageALocalServer.as(actor).listen();
    }

    toString(): string {
        return `#actor starts local server on a random port`;
    }
}

/**
 * @package
 */
class StartLocalServerOnPort extends Interaction {

    constructor(private readonly preferredPort: Answerable<number>) {
        super();
    }

    performAs(actor: UsesAbilities & CollectsArtifacts & AnswersQuestions): Promise<void> {
        return actor.answer(this.preferredPort)
            .then(port => ManageALocalServer.as(actor).listen(port));
    }

    toString(): string {
        return `#actor starts local server on port ${ this.preferredPort }`;
    }
}

/**
 * @package
 */
class StartLocalServerOnRandomPortBetween extends Interaction {

    constructor(
        private readonly lowestPort: Answerable<number>,
        private readonly highestPort: Answerable<number>,
    ) {
        super();
    }

    performAs(actor: UsesAbilities & CollectsArtifacts & AnswersQuestions): Promise<void> {
        return Promise.all([
            actor.answer(this.lowestPort),
            actor.answer(this.highestPort),
        ]).
        then(([lowestPort, highestPort]) => ManageALocalServer.as(actor).listen(lowestPort, highestPort));
    }

    toString(): string {
        return `#actor starts local server on port between ${ this.lowestPort } and ${ this.highestPort }`;
    }
}

/**
 * @package
 */
class StartLocalServerOnFirstOf extends Interaction {

    constructor(private readonly preferredPorts: Answerable<number[]>) {
        super();
    }

    performAs(actor: UsesAbilities & CollectsArtifacts & AnswersQuestions): Promise<void> {
        return actor.answer(this.preferredPorts)
            .then(preferredPorts => ManageALocalServer.as(actor).listen(preferredPorts[0]));
    }

    toString(): string {
        return `#actor starts local server on first port of ${ this.preferredPorts }`;
    }
}

import { AnswersQuestions, CollectsArtifacts, Interaction, UsesAbilities } from '@serenity-js/core';

import { ManageALocalServer } from '../abilities';

/**
 * @desc
 *  Stops the local server. Should be used in `afterEach` block to make sure that the server
 *  is correctly shut down even when the test fails.
 *
 * @extends {@serenity-js/core/lib/screenplay~Interaction}
 */
export class StopLocalServer extends Interaction {

    /**
     * Stops the server if it's running. If the server hasn't been started, this interaction does nothing.
     */
    static ifRunning(): Interaction {
        return new StopLocalServer();
    }

    /**
     * @desc
     *  Makes the provided {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  perform this {@link @serenity-js/core/lib/screenplay~Interaction}.
     *
     * @param {UsesAbilities & CollectsArtifacts & AnswersQuestions} actor
     * @returns {Promise<void>}
     *
     * @see {@link @serenity-js/core/lib/screenplay/actor~Actor}
     * @see {@link @serenity-js/core/lib/screenplay/actor~UsesAbilities}
     * @see {@link @serenity-js/core/lib/screenplay/actor~CollectsArtifacts}
     * @see {@link @serenity-js/core/lib/screenplay/actor~AnswersQuestions}
     */
    performAs(actor: UsesAbilities & CollectsArtifacts & AnswersQuestions): Promise<void> {
        return ManageALocalServer.as(actor).mapInstance(server => new Promise((resolve, reject) => {
            if (! server.address()) {
                return resolve();
            }

            server.shutdown((error: Error) =>
                error
                    ? reject(error)
                    : resolve()
            );
        }));
    }

    /**
     * @desc
     *  Generates a description to be used when reporting this {@link @serenity-js/core/lib/screenplay~Activity}.
     *
     * @returns {string}
     */
    toString(): string {
        return `#actor stops the local server`;
    }
}

import { LogicError, Question } from '@serenity-js/core';
import { AddressInfo } from 'net';
import { parse } from 'url';

import { ManageALocalServer } from '../abilities';

export class LocalServer {

    /**
     * @desc
     *  Retrieves the URL of the local server started
     *  using the {@link StartLocalServer} {@link @serenity-js/core/lib/screenplay~Interaction}.
     *
     * @returns {@serenity-js/core/lib/screenplay~Question<string>} Url of the locally running Node.js server
     *
     * @see {@link StartLocalServer}
     * @see {@link @serenity-js/rest/lib/screenplay/interactions~ChangeApiUrl}
     *
     * @returns {@serenity-js/core/lib/screenplay~Question<string>}
     */
    static url(): Question<string> {
        return Question.about<string>('the URL of the local server', actor => {
            return ManageALocalServer.as(actor).mapInstance((server, protocol) => {
                const info = server.address();

                if (! info) {
                    throw new LogicError(`The server has not been started yet`);
                }

                if (! isAddressInfo(info)) {
                    throw new LogicError(`A pipe or UNIX domain socket server does not have a URL`);
                }

                return [
                    protocol,
                    '://',
                    `${ info.family }`.toLowerCase() === 'ipv6' ? `[${ info.address }]` : info.address,
                    ':',
                    info.port,
                ].join('');
            });
        });
    }

    /**
     * @desc
     *  Retrieves the port number of the local server started
     *  using the {@link StartLocalServer} {@link @serenity-js/core/lib/screenplay~Interaction}.
     *
     * @returns {@serenity-js/core/lib/screenplay~Question<string>} port number of the locally running Node.js server
     *
     * @see {@link StartLocalServer}
     * @see {@link @serenity-js/rest/lib/screenplay/interactions~ChangeApiUrl}
     *
     * @returns {@serenity-js/core/lib/screenplay~Question<number>}
     */
    static port(): Question<number> {
        return Question.about(`local server port number`, actor => {
            const url = LocalServer.url().answeredBy(actor);

            return Number.parseInt(parse(url).port, 10);
        });
    }
}

/**
 * @param maybeAddressInfo
 * @private
 */
function isAddressInfo(maybeAddressInfo: AddressInfo | string): maybeAddressInfo is AddressInfo {
    return typeof maybeAddressInfo !== 'string'
        && !! maybeAddressInfo.address
        && !! maybeAddressInfo.family
        && !! maybeAddressInfo.port;
}

# Serenity/JS

[Serenity/JS](https://serenity-js.org) is a framework designed to make acceptance and regression testing
of modern full-stack applications faster, more collaborative and easier to scale.

Visit [serenity-js.org](https://serenity-js.org/) for the [latest tutorials](https://serenity-js.org/handbook/)
and [API docs](https://serenity-js.org/modules/), and follow [@SerenityJS](https://twitter.com/SerenityJS) and [@JanMolak](https://twitter.com/JanMolak) on Twitter for project updates.

### Learning Serenity/JS

To learn more about Serenity/JS, check out the video below, read the [tutorial](https://serenity-js.org/handbook/thinking-in-serenity-js/index.html), review the [examples](https://github.com/serenity-js/serenity-js/tree/master/examples), and create your own test suite with [Serenity/JS template projects](https://github.com/serenity-js).

If you have any questions, join us on [Serenity/JS Community Chat](https://gitter.im/serenity-js/Lobby).

[![Full-Stack Acceptance Testing with Serenity/JS and the Screenplay Pattern](https://img.youtube.com/vi/djPMf-n93Rw/0.jpg)](https://www.youtube.com/watch?v=djPMf-n93Rw)

## Serenity/JS Local Server

[`@serenity-js/local-server`](https://serenity-js.org/modules/local-server/) enables Serenity/JS Actors to manage local HTTP or HTTPS test servers powered by [Express](https://expressjs.com/),
[Hapi](https://hapijs.com/),
[Koa](https://koajs.com/),
[Restify](http://restify.com/)
or raw [Node.js](https://nodejs.org/en/docs/guides/anatomy-of-an-http-transaction/).

### Installation

To install this module, run the following command in your computer terminal:
```console
npm install --save-dev @serenity-js/{core,local-server}
```

### Example test

```
import { actorCalled } from '@serenity-js/core';
import {
    ManageALocalServer, StartLocalTestServer, StopLocalTestServer
} from '@serenity-js/local-server'
import { CallAnApi, GetRequest, Send } from '@serenity-js/rest';
import { Ensure, equals } from '@serenity-js/assertions';
import axios from 'axios';

import { requestListener } from './listener';                           (1)

const actor = actorCalled('Apisit').whoCan(
    ManageALocalServer.using(requestListener),                          (2)
    CallAnApi.using(axios.create()),
);

actor.attemptsTo(
    StartLocalTestServer.onRandomPort(),                                (3)
    Send.a(GetRequest.to(LocalServer.url())),                           (4)
    Ensure.that(LastResponse.status(), equals(200)),
    Ensure.that(LastResponse.body(), equals('Hello!')),
    StopLocalTestServer.ifRunning(),                                    (5)
);
```

In the above example:

1. A `requestListener` to be tested is imported.
2. The `Actor` is given an `Ability` to `ManageALocalServer`. This enables the `Interaction` to `StartLocalTestServer` and `StopLocalTestServer`, as well as the `LocalServer` `Question`.
3. The local server is started on a random port, although you can specify a port range if you prefer.
4. The url of the local server is retrieved and used to test an interaction with its HTTP API.
5. The local server is stopped when the test is complete. Please note that you probably want to `StopLocalTestServer` in an `afterEach` block of your test (or something equivalent) to make sure that the server is correctly shut down even when the test fails.

### Creating a server

Any `requestListener` that Node's
[`http.createServer`](https://nodejs.org/api/http.html#http_http_createserver_options_requestlistener)
or [`https.createServer`](https://nodejs.org/api/https.html#https_https_createserver_options_requestlistener)
would accept can be used with `ManageALocalServer`.

Below are example implementations of a simple HTTP server that would
satisfy the above test.

#### Raw Node.js

```javascript
// listener.js
module.exports.requestListener = (request, response) => {
  response.setHeader('Connection', 'close');
  response.end('Hello World!');
}
```

[Learn more about Node.js](https://nodejs.org/en/docs/guides/anatomy-of-an-http-transaction/).

#### Express

```javascript
// listener.js
const express = require('express');

module.exports.requestListener = express().
    get('/', (req: express.Request, res: express.Response) => {
        res.status(200).send('Hello World!');
    });
```

[Learn more about Express](https://expressjs.com/).

#### HAPI

```javascript
// listener.js
const hapi = require('@hapi/hapi');

const server = new hapi.Server();
server.route({ method: 'GET', path: '/', handler: (req, h) => 'Hello World!' })

module.exports.requestListener = server.listener;
```

[Learn more about HAPI](https://hapijs.com/).

#### Koa

```javascript
// listener.js
const Koa = require('koa');

module.exports.requestListener = new Koa()
    .use(ctx => Promise.resolve(ctx.body = 'Hello World!'))
    .callback();
```

[Learn more about Koa](https://koajs.com/).

#### Restify

```javascript
// listener.js
const restify = require('restify');

const server = restify.createServer(options);

server.get('/', (req, res, next) => {
    res.send('Hello World!');
});

module.exports.requestListener = server;
```

[Learn more about Restify](http://restify.com/).

import 'mocha';

import { EventRecorder, expect, PickEvent } from '@integration/testing-tools';
import { endsWith, Ensure, equals, not, startsWith } from '@serenity-js/assertions';
import { Actor, actorCalled, Cast, configure } from '@serenity-js/core';
import { ActivityFinished, ActivityStarts } from '@serenity-js/core/lib/events';
import { CallAnApi, GetRequest, LastResponse, Send } from '@serenity-js/rest';
import axios from 'axios';

import { LocalServer, ManageALocalServer, StartLocalServer, StopLocalServer } from '../src';

describe('@serenity-js/local-server', () => {

    class Actors implements Cast {
        prepare(actor: Actor): Actor {
            return actor.whoCan(
                ManageALocalServer.runningAHttpListener(function (request, response) {
                    response.setHeader('Connection', 'close');
                    response.end('Hello World!');
                }),
                CallAnApi.using(axios.create()),
            );
        }
    }

    let recorder: EventRecorder;

    beforeEach(() => {

        recorder = new EventRecorder();

        configure({
            actors: new Actors(),
            crew: [ recorder ],
        });
    });

    describe('when managing a local server', () => {

        /**
         * @test {ManageALocalServer}
         * @test {StartLocalServer}
         * @test {StopLocalServer}
         */
        it(`correctly reports actor's activities`, () => expect(actorCalled('Nadia').attemptsTo(
            StartLocalServer.onRandomPort(),
            Ensure.that(LocalServer.url(), startsWith('http://127.0.0.1')),
            Send.a(GetRequest.to(LocalServer.url())),
            Ensure.that(LastResponse.status(), equals(200)),
            Ensure.that(LastResponse.body<string>(), equals('Hello World!')),
            StopLocalServer.ifRunning(),
        )).to.be.fulfilled.then(() => {

            PickEvent.from(recorder.events)
                .next(ActivityStarts,   hasName(`Nadia starts local server on a random port`))
                .next(ActivityFinished, hasName(`Nadia starts local server on a random port`))
                .next(ActivityFinished, hasName(`Nadia ensures that the URL of the local server does start with 'http://127.0.0.1'`))
                .next(ActivityFinished, hasName(`Nadia sends a GET request to the URL of the local server`))
                .next(ActivityFinished, hasName(`Nadia ensures that the status of the last response does equal 200`))
                .next(ActivityFinished, hasName(`Nadia ensures that the body of the last response does equal 'Hello World!'`))
                .next(ActivityStarts,   hasName(`Nadia stops the local server`))
                .next(ActivityFinished, hasName(`Nadia stops the local server`))
            ;
        }));
    });

    describe('when managing a local server', () => {

        let Nadia: Actor, Phillip: Actor;

        const port = 5555;

        beforeEach(() =>  {
            Nadia = actorCalled('Nadia');
            Phillip = actorCalled('Phillip');
        });

        it(`it falls back to a random port if the preferred one is taken`, () =>
            expect(Nadia.attemptsTo(
                StartLocalServer.onOneOfThePreferredPorts([port]),
                Ensure.that(LocalServer.url(), endsWith(`${ port }`)),
            ).then(() => Phillip.attemptsTo(
                StartLocalServer.onOneOfThePreferredPorts([port]),
                Ensure.that(LocalServer.url(), not(endsWith(`${ port }`))),
            ))).to.be.fulfilled);

        afterEach(() => Promise.all([
            Nadia.attemptsTo(StopLocalServer.ifRunning()),
