import { LogicError } from '@serenity-js/core';
import { ModuleLoader, TestRunnerAdapter } from '@serenity-js/core/lib/io';
import { ExecutionIgnored, ImplementationPending, Outcome } from '@serenity-js/core/lib/model';
import * as fs from 'fs';
import * as path from 'path'; // eslint-disable-line unicorn/import-style

import { MochaConfig } from './MochaConfig';
import type Mocha = require('mocha');

/**
 * @desc
 *  Allows for programmatic execution of Mocha test scenarios,
 *  using {@link SerenityReporterForMocha} to report progress.
 *
 * @implements {@serenity-js/core/lib/io~TestRunnerAdapter}
 */
export class MochaAdapter implements TestRunnerAdapter {

    private mocha: Mocha;
    private totalScenarios: number;

    /**
     * @desc
     *  test
     * @param {MochaConfig} config
     * @param {@serenity-js/core/lib/io~ModuleLoader} loader
     */
    constructor(
        private readonly config: MochaConfig,
        private readonly loader: ModuleLoader,
    ) {
    }

    /**
     * @desc
     *  Scenario success threshold for this test runner.
     *
     * @returns {Outcome | { Code: number }}
     */
    successThreshold(): Outcome | { Code: number } {
        return this.config.strict
            ? ExecutionIgnored
            : ImplementationPending;
    }

    /**
     * @desc
     *  Loads test scenarios.
     *
     * @param {string[]} pathsToScenarios
     *
     * @returns {Promise<void>}
     */
    async load(pathsToScenarios: string[]): Promise<void> {
        const _Mocha = this.loader.require('mocha');

        this.mocha = new _Mocha({
            ...this.config,
            reporter: require.resolve('../index'),
        });

        this.mocha.fullTrace();

        this.mocha.files = pathsToScenarios;

        this.mocha.suite.on('pre-require', (context: Mocha.MochaGlobals, file: string, mocha: Mocha) => {
            this.requireAny(this.config.require);
        });

        await this.mocha.loadFilesAsync()

        const mochaRunner = new _Mocha.Runner(this.mocha.suite, {
            delay: false,
        });

        if (this.config.grep) {
            mochaRunner.grep(this.mocha.options.grep as RegExp, this.config.invert);
        }

        this.totalScenarios = mochaRunner.total;
    }

    /**
     * @desc
     *  Returns the number of loaded scenarios
     *
     * @throws {@serenity-js/core/lib/errors~LogicError}
     *  If called before `load`
     *
     * @returns {number}
     */
    scenarioCount(): number {
        if (this.totalScenarios === undefined) {
            throw new LogicError('Make sure to call `load` before calling `scenarioCount`');
        }

        return this.totalScenarios;
    }

    /**
     * @desc
     *  Runs loaded test scenarios.
     *
     * @throws {LogicError}
     *  If called before `load`
     *
     *
     * @returns {Promise<void>}
     */
    run(): Promise<void> {
        return new Promise((resolve, reject) => {
            if (this.mocha === undefined) {
                throw new LogicError('Make sure to call `load` before calling `run`');
            }

            this.mocha.run(numberOfFailures => resolve())
        });
    }

    private requireAny(filesOrModules: string | string[]) {
        const requires = filesOrModules
            ? [].concat(filesOrModules).filter(item => !! item)
            : [];

        requires.forEach(fileOrModule => {
            const required = fs.existsSync(fileOrModule) || fs.existsSync(`${ fileOrModule }.js`)
                ? path.resolve(fileOrModule)    // local file
                : fileOrModule;                 // module

            require(required);
        });
    }
}

/**
 * @desc
 *  Configuration object for the Mocha test runner.
 *
 * @see https://github.com/mochajs/mocha/blob/v8.0.1/example/config/.mocharc.yml
 *
 * @public
 */
export interface MochaConfig {

    /**
     * @desc
     *  Path to config file.
     *
     * @see https://github.com/mochajs/mocha/tree/v8.0.1/example/config
     *
     * @type {string | undefined}
     * @public
     */
    config?: string;

    /**
     * @desc
     *  Allow uncaught errors to propagate.
     *
     * @type {boolean | undefined}
     * @public
     */
    'allow-uncaught'?: boolean;

    /**
     * @desc
     *  Require all tests to use a callback (async) or return a Promise.
     *
     * @type {boolean | undefined}
     * @public
     */
    'async-only'?: boolean;

    /**
     * @desc
     *  Abort ("bail") after first test failure.
     *
     * @type {boolean | undefined}
     * @public
     */
    bail?: boolean;

    /**
     * @desc
     *  Check for global variable leaks.
     *
     * @type {boolean | undefined}
     * @public
     */
    'check-leaks'?: boolean;

    /**
     * @desc
     *  Delay initial execution of root suite.
     *
     * @type {boolean | undefined}
     * @public
     */
    delay?: boolean;

    /**
     * @desc
     *  Only run tests containing this string.
     *  Please note: {@link MochaConfig.fgrep} and {@link MochaConfig.grep} are mutually exclusive.
     *
     * @type {string | undefined}
     * @public
     */
    fgrep?: string;

    /**
     * @desc
     *  File(s) to be loaded prior to root suite execution.
     *
     * @type {string[] | undefined}
     * @public
     */
    file?: string[];

    /**
     * @desc
     *  Fail if exclusive test(s) encountered.
     *
     * @type {boolean | undefined}
     * @public
     */
    'forbid-only'?: boolean;

    /**
     * @desc
     *  Fail if pending test(s) encountered.
     *
     * @type {boolean | undefined}
     * @public
     */
    'forbid-pending': boolean;

    /**
     * @desc
     *  List of allowed global variables.
     *
     * @type {string[] | undefined}
     * @public
     */
    global?: string[];

    /**
     * @desc
     *  Only run tests matching this string or regexp.
     *  Please note: {@link MochaConfig.fgrep} and {@link MochaConfig.grep} are mutually exclusive.
     *
     * @type {string | RegExp | undefined}
     * @public
     */
    grep?: string | RegExp;

    /**
     * @desc
     *  Enable Growl notifications.
     *
     * @type {boolean | undefined}
     * @public
     */
    growl?: boolean;

    /**
     * @desc
     *  Inverts {@link MochaConfig.grep} and {@link MochaConfig.fgrep} matches.
     *
     * @type {boolean | undefined}
     * @public
     */
    invert?: boolean;

    /**
     * @desc
     *  Require module.
     *
     * @type {string[] | undefined}
     * @public
     */
    require?: string[];

    /**
     * @desc
     *  Retry failed tests this many times.
     *
     * @type {number | undefined}
     * @public
     */
    retries?: number;

    /**
     * @desc
     *  Specify "slow" test threshold (in milliseconds).
     *
     * @type {number} [slow=75]
     * @public
     */
    slow?: number;

    /**
     * @desc
     *  Specify test timeout threshold (in milliseconds).
     *  Please note: setting this property to 0 means "no timeout".
     *
     * @type {number} [timeout=2000]
     * @public
     */
    timeout?: number;

    /**
     * @desc
     *  Specify user interface.
     *
     * @see https://mochajs.org/#interfaces
     *
     * @type {string} [ui="bdd"]
     * @public
     */
    ui?: string;

    /**
     * @desc
     *  When set to `true`, a skipped test is considered a failure.
     *
     * @see https://mochajs.org/#interfaces
     *
     * @type {boolean} [strict=false]
     * @public
     */
    strict?: boolean;
}

