import { SerenityConfig } from '@serenity-js/core';
import { Config as ProtractorConfig } from 'protractor';

/**
 * @desc
 *  Protractor configuration object with an additional
 *  section to configure the Serenity/JS framework.
 *
 * @public
 * @interface
 *
 * @see {@link @serenity-js/core~SerenityConfig}
 *
 * @see https://github.com/angular/protractor/blob/master/lib/config.ts
 * @see {@link @serenity-js/core~configure}
 */
export interface Config extends ProtractorConfig {
    serenity: SerenityConfig & { runner?: string };
}

import { ArtifactArchiver, Serenity } from '@serenity-js/core';
import { isPlainObject } from 'is-plain-object'; // fails when using default import
import { protractor, Runner } from 'protractor';
import deepmerge = require('deepmerge');

import { BrowserDetector, StandardisedCapabilities } from './browser-detector';
import { Config } from './Config';
import { ProtractorReport, ProtractorReporter } from './reporter';
import { TestRunnerDetector } from './runner';

/**
 * @private
 */
export class ProtractorFrameworkAdapter {
    constructor(
        private readonly serenity: Serenity,
        private readonly protractorRunner: Runner,
        private readonly detector: TestRunnerDetector,
    ) {
    }

    /**
     * @param {string[]} specs
     *  Paths to spec files
     *
     * @return {Promise<ProtractorReport>}
     */
    run(specs: string[]): Promise<ProtractorReport> {

        const runner    = this.detector.runnerFor(this.protractorRunner.getConfig());
        const reporter  = new ProtractorReporter(this.protractorRunner, runner.successThreshold());

        const config    = deepmerge<Config>(this.defaultConfig(), this.protractorRunner.getConfig(), {
            isMergeableObject: isPlainObject,
        });

        this.serenity.configure({
            cueTimeout:     config.serenity.cueTimeout,
            actors:         config.serenity.actors,
            crew:           [
                BrowserDetector.with(StandardisedCapabilities.of(() => protractor.browser)),
                ...config.serenity.crew,
                reporter,
            ],
        });

        return Promise.resolve()
            .then(() => this.protractorRunner.runTestPreparer(TestRunnerDetector.protractorCliOptions()))
            .then(() => runner.load(specs))
            .then(() => runner.run())
            .then(() => (config.onComplete || noop)())
            .then(() => reporter.report());
    }

    private defaultConfig(): Config {
        return {
            serenity: {
                crew: [
                    ArtifactArchiver.storingArtifactsAt(process.cwd(), 'target/site/serenity'),
                ],
            },
        };
    }
}

/** @private */
function noop() {
    // no-op
}

import { Stage } from '@serenity-js/core';
import { AsyncOperationAttempted, AsyncOperationCompleted, DomainEvent, SceneStarts, SceneTagged } from '@serenity-js/core/lib/events';
import { BrowserTag, CorrelationId, Description, PlatformTag } from '@serenity-js/core/lib/model';
import { StageCrewMember } from '@serenity-js/core/lib/stage';

import { StandardisedCapabilities } from './StandardisedCapabilities';

/**
 * @private
 *
 * @see https://github.com/serenity-js/serenity-js/issues/455
 * @see https://github.com/serenity-bdd/serenity-core/pull/1860/files
 * @see https://github.com/serenity-js/serenity-js/issues/132
 */
export class BrowserDetector implements StageCrewMember {

    static with(capabilities: StandardisedCapabilities): BrowserDetector {
        return new BrowserDetector(capabilities);
    }

    constructor(
        private readonly capabilities: StandardisedCapabilities,
        private stage?: Stage,
    ) {
    }

    assignedTo(stage: Stage): StageCrewMember {
        return new BrowserDetector(this.capabilities, stage);
    }

    notifyOf(event: DomainEvent): void {
        if (event instanceof SceneStarts) {
            const id = CorrelationId.create();

            this.stage.announce(new AsyncOperationAttempted(
                new Description(`[${ this.constructor.name }] Detecting web browser details...`),
                id,
                this.stage.currentTime(),
            ));

            Promise.all([
                this.capabilities.browserName(),
                this.capabilities.browserVersion(),
                this.capabilities.platformName(),
                this.capabilities.platformVersion(),
            ]).
            then(([browserName, browserVersion, platformName, platformVersion]) => {

                this.stage.announce(new SceneTagged(
                    event.sceneId,
                    new BrowserTag(browserName, browserVersion),
                    this.stage.currentTime(),
                ));

                this.stage.announce(new SceneTagged(
                    event.sceneId,
                    new PlatformTag(platformName, platformVersion),
                    this.stage.currentTime(),
                ));

                this.stage.announce(new AsyncOperationCompleted(
                    new Description(`[${ this.constructor.name }] Detected web browser details`),
                    id,
                    this.stage.currentTime(),
                ));
            });
        }
    }
}

import { Capabilities, ProtractorBrowser } from 'protractor';

/**
 * @private
 */
export class StandardisedCapabilities {
    static of(currentBrowser: () => ProtractorBrowser): StandardisedCapabilities {
        return new StandardisedCapabilities(currentBrowser);
    }

    constructor(private currentBrowser: () => ProtractorBrowser) {
    }

    browserName(): PromiseLike<string> {
        return this.get(
            caps => caps.get('browserName'),
        );
    }

    browserVersion(): PromiseLike<string> {
        return this.get(
            caps => caps.get('version'),
            caps => caps.get('browserVersion'),
            caps => caps.has('deviceManufacturer') && caps.has('deviceModel')
                ? `${ caps.get('deviceManufacturer') } ${ caps.get('deviceModel') }`
                : undefined,
            caps => caps.has('mobile') && caps.get('mobile').version,
        ).
        then(version =>
            this.get(
                caps => !! caps.get('mobileEmulationEnabled') && '(mobile emulation)',
            ).then(suffix =>
                [
                    version,
                    suffix,
                ].filter(_ => !!_).join(' '),
            ),
        );
    }

    platformName(): PromiseLike<string> {
        return this.get(
            caps => (!! caps.get('platformName') && ! /any/i.test(caps.get('platformName')))
                ? caps.get('platformName')
                : caps.get('platform'),
        );
    }

    platformVersion(): PromiseLike<string> {
        return this.get(
            caps => caps.get('platformVersion'),
        );
    }

    private get(...fetchers: Array<(capabilities: Capabilities) => string>): PromiseLike<string> {
        return this.currentBrowser().getCapabilities().then(caps => {
            for (const fetcher of fetchers) {
                const result = fetcher(caps);
                if (result) {
                    return result;
                }
            }
            return undefined;   // eslint-disable-line unicorn/no-useless-undefined
        });
    }
}

export * from './ProtractorFrameworkAdapter';
export * from './run';
export * from './runner';

import { SpecResult } from 'protractor/built/plugins';

/**
 * @desc
 *  A JSON object which needs to be returned to Protractor when {@link ProtractorFrameworkAdapter}
 *  is done with executing the test scenarios.
 *
 * @see https://github.com/angular/protractor/blob/master/lib/frameworks/README.md
 *
 * @private
 */
export interface ProtractorReport {
    /**
     * Total number of failed scenarios
     */
    failedCount: number;

    /**
     * Results per scenario
     *
     * Note: duration is not part of the SpecResult, but is being used by the Jasmine reporter
     * https://github.com/angular/protractor/blob/4f74a4ec753c97adfe955fe468a39286a0a55837/lib/frameworks/jasmine.js#L32
     */
    specResults: Array<SpecResult & { duration: number }>;
}

import { Stage } from '@serenity-js/core';
import { AsyncOperationAttempted, AsyncOperationCompleted, AsyncOperationFailed, DomainEvent, SceneFinished, SceneFinishes, SceneStarts } from '@serenity-js/core/lib/events';
import { CorrelationId, Description, ExecutionSkipped, Outcome, ProblemIndication, Timestamp } from '@serenity-js/core/lib/model';
import { StageCrewMember } from '@serenity-js/core/lib/stage';
import { Runner } from 'protractor';

import { ProtractorReport } from './ProtractorReport';

/**
 * @private
 */
export class ProtractorReporter implements StageCrewMember {
    private readonly startTime: { [key: string ]: Timestamp } = {};

    constructor(
        private readonly runner: Runner,
        private readonly successThreshold: Outcome | { Code: number } = ExecutionSkipped,
        private readonly reported: ProtractorReport = { failedCount: 0, specResults: [] },
        private stage?: Stage,
    ) {
    }

    assignedTo(stage: Stage): StageCrewMember {
        return new ProtractorReporter(this.runner, this.successThreshold, this.reported, stage);
    }

    notifyOf(event: DomainEvent): void {
        if (event instanceof SceneStarts) {
            this.recordStart(event);
        }

        else if (event instanceof SceneFinishes) {
            this.afterEach();
        }

        else if (event instanceof SceneFinished && event.outcome.isWorseThan(this.successThreshold)) {
            this.recordFailure(event);

            this.runner.emit('testFail', {
                name: event.details.name.value,
                category: event.details.category.value,
            });
        }
        else if (event instanceof SceneFinished && ! event.outcome.isWorseThan(this.successThreshold)) {
            this.recordSuccess(event);

            this.runner.emit('testPass', {
                name:       event.details.name.value,
                category:   event.details.category.value,
            });
        }
    }

    report(): ProtractorReport {
        return this.reported;
    }

    private recordFailure(event: SceneFinished) {
        const outcome = (event.outcome as ProblemIndication);

        this.reported.failedCount++;

        this.reported.specResults.push({
            description: `${ event.details.category.value } ${ event.details.name.value }`,
            duration: event.timestamp.diff(this.startTime[event.details.toString()]).inMilliseconds(),
            assertions: [{
                passed: false,
                errorMsg: outcome.error.message,
                stackTrace: outcome.error.stack,
            }],
        });
    }

    private recordStart(event: SceneStarts) {
        this.startTime[event.details.toString()] = event.timestamp;
    }

    private recordSuccess(event: SceneFinished) {
        this.reported.specResults.push({
            description: `${ event.details.category.value } ${ event.details.name.value }`,
            duration: event.timestamp.diff(this.startTime[event.details.toString()]).inMilliseconds(),
            assertions: [{
                passed: true,
            }],
        });
    }

    private afterEach(): PromiseLike<void> {
        if (! this.runner.afterEach) {
            return Promise.resolve();
        }

        const id = CorrelationId.create();

        this.stage.announce(new AsyncOperationAttempted(
            new Description(`[${ this.constructor.name }] Invoking ProtractorRunner.afterEach...`),
            id,
            this.stage.currentTime(),
        ));

        return Promise.resolve(this.runner.afterEach() as PromiseLike<void> | undefined)
            .then(
                () =>
                    this.stage.announce(new AsyncOperationCompleted(
                        new Description(`[${ this.constructor.name }] ProtractorRunner.afterEach succeeded`),
                        id,
                        this.stage.currentTime(),
                    )),
                error =>
                    this.stage.announce(new AsyncOperationFailed(error, id, this.stage.currentTime())),
            );
    }
}

/* istanbul ignore file */

import { serenity } from '@serenity-js/core';
import { Path } from '@serenity-js/core/lib/io';
import { Runner } from 'protractor';

import { ProtractorFrameworkAdapter } from './ProtractorFrameworkAdapter';
import { ProtractorReport } from './reporter';
import { TestRunnerDetector, TestRunnerLoader } from './runner';

/**
 * @param {protractor~Runner} runner
 * @param {string[]} specs
 * @returns {Promise<ProtractorReport>}
 *
 * @see https://github.com/angular/protractor/blob/master/lib/frameworks/README.md#framework-adapters-for-protractor
 */
export function run(runner: Runner, specs: string[]): Promise<ProtractorReport> {
    const cwd = Path.from(runner.getConfig().configDir);

    return new ProtractorFrameworkAdapter(
        serenity,
        runner,
        new TestRunnerDetector(new TestRunnerLoader(cwd, process.pid)),
    ).run(specs);
}

/**
 * @public
 * @interface
 */
export interface CucumberAdapterConfig {
    /**
     * @desc
     *  Instruct Serenity/JS Cucumber Adapter to take over the stdout output of native Cucumber reporters to prevent them from clobbering the log
     *
     * @type {boolean}
     */
    useStandardOutput: boolean;

    /**
     * @desc
     *  Add `runnerId` to names of output files produced by native Cucumber.js formatters
     *
     * @type {boolean}
     */
    uniqueFormatterOutputs: boolean;
}

import { TestRunnerAdapter } from '@serenity-js/core/lib/io';
import { Config as ProtractorConfig } from 'protractor';

import { TestRunnerLoader } from './TestRunnerLoader';

/**
 * @desc
 *  Detects the {@link @serenity-js/core/lib/io~TestRunnerAdapter} to use,
 *  based on Protractor configuration.
 *
 * @public
 */
export class TestRunnerDetector {

    static cucumberOpts = 'cucumberOpts';
    static jasmineNodeOpts = 'jasmineNodeOpts';
    static mochaOpts = 'mochaOpts';

    static protractorCliOptions(): string[] {
        return [
            TestRunnerDetector.cucumberOpts,
            TestRunnerDetector.jasmineNodeOpts,
            TestRunnerDetector.mochaOpts,
        ];
    }

    /**
     * @param {TestRunnerLoader} testRunnerLoader
     */
    constructor(private readonly testRunnerLoader: TestRunnerLoader) {
    }

    /**
     * @param {protractor~ProtractorConfig} config
     * @returns {TestRunnerAdapter}
     */
    runnerFor(config: ProtractorConfig): TestRunnerAdapter {

        const
            specifiesRunnerFor = (type: string) =>
                !!config.serenity &&
                !!config.serenity.runner &&
                config.serenity.runner === type;

        switch (true) {
            case specifiesRunnerFor('cucumber'):
                return this.useCucumber(config);
            case specifiesRunnerFor('jasmine'):
                return this.useJasmine(config);
            case specifiesRunnerFor('mocha'):
                return this.useMocha(config);
            case !! config.cucumberOpts:
                return this.useCucumber(config);
            case !! config.mochaOpts:
                return this.useMocha(config);
            case !! config.jasmineNodeOpts:     // eslint-disable-line unicorn/no-useless-switch-case
            default:
                return this.useJasmine(config);
        }
    }

    private useJasmine(config: ProtractorConfig): TestRunnerAdapter {
        return this.testRunnerLoader.forJasmine(this.mergedConfigFor(config, TestRunnerDetector.jasmineNodeOpts));
    }

    private useMocha(config: ProtractorConfig): TestRunnerAdapter {
        return this.testRunnerLoader.forMocha(this.mergedConfigFor(config, TestRunnerDetector.mochaOpts));
    }

    private useCucumber(config: ProtractorConfig): TestRunnerAdapter {

        const serenityReportingServicesConfigured  = config?.serenity?.crew?.length > 0;

        return this.testRunnerLoader.forCucumber(this.mergedConfigFor(config, TestRunnerDetector.cucumberOpts), {
            useStandardOutput:      serenityReportingServicesConfigured,
            uniqueFormatterOutputs: this.multiCapabilitiesOrTestShardingEnabled(config),
        })
    }

    private mergedConfigFor<K extends keyof ProtractorConfig>(config: ProtractorConfig = {}, key: K): ProtractorConfig[K] {

        return Object.assign(
            {},
            config[key],
            (config.capabilities || {})[key],
        );
    }

    private multiCapabilitiesOrTestShardingEnabled(config: ProtractorConfig): boolean {
        return !! (
            (Array.isArray(config.multiCapabilities) && config.multiCapabilities.length > 0)
            || typeof config.getMultiCapabilities === 'function'
            || config.capabilities?.shardTestFiles
        );
    }
}

/* eslint-disable unicorn/prevent-abbreviations,@typescript-eslint/ban-types */
import { Config, FileFinder, FileSystem, ModuleLoader, Path, TestRunnerAdapter } from '@serenity-js/core/lib/io';
import { CucumberConfig } from '@serenity-js/cucumber/lib/cli';

import { CucumberAdapterConfig } from './CucumberAdapterConfig';

/**
 * @desc
 *  Loads a {@link @serenity-js/core/lib/io~TestRunnerAdapter}, needed to invoke
 *  the chosen test runner programmatically.
 *
 * @public
 */
export class TestRunnerLoader {

    private readonly moduleLoader: ModuleLoader;
    private readonly fileSystem: FileSystem;
    private readonly finder: FileFinder;

    /**
     * @param {@serenity-js/core/lib/io~Path} cwd
     *  Current working directory; used to resolve glob paths to files for Cucumber.js to `require`,
     *  and instructing Serenity/JS where to look for this module's optional
     *  dependencies, like [`@serenity-js/cucumber`](/modules/cucumber),
     *  [`@serenity-js/mocha`](/modules/mocha),
     *  [`@serenity-js/jasmine`](/modules/jasmine), etc.
     *
     * @param {number | string} runnerId
     *  Unique identifier used to differentiate output files produced by native Cucumber.js formatters.
     *  For example, `process.pid`
     */
    constructor(cwd: Path, private readonly runnerId: number | string) {
        this.moduleLoader   = new ModuleLoader(cwd.value);
        this.fileSystem     = new FileSystem(cwd);
        this.finder         = new FileFinder(cwd);
    }

    /**
     * @param {@serenity-js/jasmine/lib/adapter~JasmineConfig} jasmineNodeOpts
     * @returns {@serenity-js/core/lib/io~TestRunnerAdapter}
     */
    forJasmine(jasmineNodeOpts: object /* JasmineConfig */): TestRunnerAdapter {
        const { JasmineAdapter } = this.moduleLoader.require('@serenity-js/jasmine/lib/adapter')
        return new JasmineAdapter(jasmineNodeOpts, this.moduleLoader);
    }

    /**
     * @param {@serenity-js/mocha/lib/adapter~MochaConfig} mochaOpts
     * @returns {@serenity-js/core/lib/io~TestRunnerAdapter}
     */
    forMocha(mochaOpts: object /* MochaConfig */): TestRunnerAdapter {
        const { MochaAdapter } = this.moduleLoader.require('@serenity-js/mocha/lib/adapter')
        return new MochaAdapter(mochaOpts, this.moduleLoader);
    }

    /**
     *
     * @param {@serenity-js/cucumber/lib/cli~CucumberConfig} cucumberOpts
     * @param {CucumberAdapterConfig} adapterConfig
     * @returns {@serenity-js/core/lib/io~TestRunnerAdapter}
     */
    forCucumber(cucumberOpts: object /* CucumberConfig */, adapterConfig: CucumberAdapterConfig): TestRunnerAdapter {
        const { CucumberCLIAdapter, CucumberFormat, StandardOutput, TempFileOutput } = this.moduleLoader.require('@serenity-js/cucumber/lib/cli');

        const config = new Config<CucumberConfig>(cucumberOpts)
            .where('require', requires =>
                this.finder.filesMatching(requires).map(p => p.value)
            )
            .whereIf(adapterConfig.uniqueFormatterOutputs, 'format', values =>
                [].concat(values).map(value => {
                    const format = new CucumberFormat(value);

                    if (format.output === '') {
                        return format.value;
                    }

                    const basename = Path.from(format.output).basename();
                    const filenameParts = basename.split('.');

                    if (filenameParts[0] === basename) {
                        return `${ format.formatter }:${ format.output }.${ this.runnerId }`;
                    }

                    filenameParts.splice(-1, 0, `${ this.runnerId }`);

                    return `${ format.formatter }:${ format.output.replace(basename, filenameParts.join('.')) }`;
                })
            );

        const output = adapterConfig.useStandardOutput
            ? new StandardOutput()
            : new TempFileOutput(this.fileSystem);

        return new CucumberCLIAdapter(config.object(), this.moduleLoader, this.fileSystem, output);
    }
}

export * from './CucumberAdapterConfig';
export * from './TestRunnerDetector';
export * from './TestRunnerLoader';

import { AnswersQuestions, Expectation, ExpectationMet, ExpectationNotMet, ExpectationOutcome } from '@serenity-js/core';
import { ElementFinder } from 'protractor';

import { promiseOf } from '../promiseOf';

/**
 * @access private
 */
export class ElementFinderExpectation extends Expectation<any, ElementFinder> {
    static forElementTo(message: string, fn: (actual: ElementFinder) => PromiseLike<boolean>): Expectation<any, ElementFinder> {
        return new ElementFinderExpectation(message, fn);
    }

    constructor(
        subject: string,
        private readonly fn: (actual: ElementFinder) => PromiseLike<boolean>,
    ) {
        super(subject);
    }

    answeredBy(actor: AnswersQuestions): (actual: ElementFinder) => Promise<ExpectationOutcome<boolean, ElementFinder>> {

        return (actual: ElementFinder) =>
            promiseOf(this.fn(actual)).then(_ => _
                ? new ExpectationMet(this.toString(), undefined, actual)
                : new ExpectationNotMet(this.toString(), undefined, actual),
            );
    }
}

export * from './isActive';
export * from './isClickable';
export * from './isEnabled';
export * from './isPresent';
export * from './isSelected';
export * from './isVisible';

import { Expectation } from '@serenity-js/core';
import { ElementFinder, WebElement } from 'protractor';

import { ElementFinderExpectation } from './ElementFinderExpectation';

/**
 * @desc
 *  Expectation that the element is active.
 *
 * @returns {@serenity-js/core/lib/screenplay/questions~Expectation<boolean, ElementFinder>}
 *
 * @see {@link @serenity-js/assertions~Ensure}
 * @see {@link @serenity-js/core/lib/screenplay/questions~Check}
 * @see {@link Wait}
 */
export function isActive(): Expectation<boolean, ElementFinder> {
    return ElementFinderExpectation.forElementTo('become active', (actual: ElementFinder) =>
        actual.getWebElement().then(element =>
            element.getDriver().switchTo().activeElement().then((active: WebElement) =>
                actual.equals(active),
            ),
        ),
    );
}

import { and } from '@serenity-js/assertions';
import { Expectation } from '@serenity-js/core';
import { ElementFinder } from 'protractor';

import { isEnabled } from './isEnabled';
import { isVisible } from './isVisible';

/**
 * @desc
 *  Expectation that the element is visible and enabled, and therefore clickable
 *
 * @returns {@serenity-js/core/lib/screenplay/questions~Expectation<boolean, ElementFinder>}
 *
 * @see {@link @serenity-js/assertions~Ensure}
 * @see {@link @serenity-js/core/lib/screenplay/questions~Check}
 * @see {@link Wait}
 */
export function isClickable(): Expectation<any, ElementFinder> {
    return Expectation.to<ElementFinder>('become clickable').soThatActual(and(isVisible(), isEnabled()));
}

import { Expectation } from '@serenity-js/core';
import { ElementFinder } from 'protractor';

import { ElementFinderExpectation } from './ElementFinderExpectation';

/**
 * @desc
 *  Expectation that the element is enabled.
 *
 * @returns {@serenity-js/core/lib/screenplay/questions~Expectation<boolean, ElementFinder>}
 *
 * @see {@link @serenity-js/assertions~Ensure}
 * @see {@link @serenity-js/core/lib/screenplay/questions~Check}
 * @see {@link Wait}
 */
export function isEnabled(): Expectation<boolean, ElementFinder> {
    return ElementFinderExpectation.forElementTo('become enabled', actual => actual.isEnabled());
}

import { Expectation } from '@serenity-js/core';
import { ElementFinder } from 'protractor';

import { ElementFinderExpectation } from './ElementFinderExpectation';

/**
 * @desc
 *  Expectation that the element is present in the DOM of a page.
 *  Please note that this does not necessarily mean that the element is visible.
 *
 * @returns {@serenity-js/core/lib/screenplay/questions~Expectation<boolean, ElementFinder>}
 *
 * @see {@link @serenity-js/assertions~Ensure}
 * @see {@link @serenity-js/core/lib/screenplay/questions~Check}
 * @see {@link Wait}
 */
export function isPresent(): Expectation<boolean, ElementFinder> {
    return ElementFinderExpectation.forElementTo('become present', actual => actual.isPresent());
}

import { and } from '@serenity-js/assertions';
import { Expectation } from '@serenity-js/core';
import { ElementFinder } from 'protractor';

import { ElementFinderExpectation } from './ElementFinderExpectation';
import { isPresent } from './isPresent';

/**
 * @desc
 *  Expectation that the element is present and selected
 *
 * @returns {@serenity-js/core/lib/screenplay/questions~Expectation<boolean, ElementFinder>}
 *
 * @see {@link @serenity-js/assertions~Ensure}
 * @see {@link @serenity-js/core/lib/screenplay/questions~Check}
 * @see {@link Wait}
 */
export function isSelected(): Expectation<any, ElementFinder> {
    return Expectation.to<ElementFinder>('become selected').soThatActual(and(
        isPresent(),
        ElementFinderExpectation.forElementTo('become selected', actual => actual.isSelected()),
    ));
}

import { and } from '@serenity-js/assertions';
import { Expectation } from '@serenity-js/core';
import { ElementFinder } from 'protractor';

import { ElementFinderExpectation } from './ElementFinderExpectation';
import { isPresent } from './isPresent';

/**
 * @desc
 *  Expectation that the element is present in the DOM of the page and visible.
 *
 * @returns {@serenity-js/core/lib/screenplay/questions~Expectation<boolean, ElementFinder>}
 *
 * @see {@link @serenity-js/assertions~Ensure}
 * @see {@link @serenity-js/core/lib/screenplay/questions~Check}
 * @see {@link Wait}
 */
export function isVisible(): Expectation<any, ElementFinder> {
    return Expectation.to<ElementFinder>('become visible').soThatActual(and(
        isPresent(),
        isDisplayed(),
    ));
}

function isDisplayed(): Expectation<any, ElementFinder> {
    return ElementFinderExpectation.forElementTo('become displayed', actual => actual.isDisplayed());
}

export * from './expectations';
export * from './screenplay';
export * from './stage';

/**
 * @desc
 *  Wraps Webdriver promise into a Node-native promise to allow for correct promise chaining.
 *
 * @private
 *
 * @param promiseLike
 * @returns {Promise<T>}
 */
export function promiseOf<T>(promiseLike: PromiseLike<T>): Promise<T> {
    return new Promise((resolve, reject) => {
        promiseLike.then(resolve, reject);
    });
}

import { Ability, ConfigurationError, LogicError, UsesAbilities } from '@serenity-js/core';
import { ActionSequence, ElementArrayFinder, ElementFinder, Locator, ProtractorBrowser } from 'protractor';
import { AlertPromise, Capabilities, Navigation, Options, WebElement, WebElementPromise } from 'selenium-webdriver';

import { promiseOf } from '../../promiseOf';

/**
 * @desc
 *  An {@link @serenity-js/core/lib/screenplay~Ability} that enables the {@link Actor} to interact with web front-ends using {@link protractor}.
 *
 * @example <caption>Using the protractor.browser</caption>
 *  import { Actor } from '@serenity-js/core';
 *  import { BrowseTheWeb, Navigate, Target } from '@serenity-js/protractor'
 *  import { Ensure, equals } from '@serenity-js/assertions';
 *  import { by, protractor } from 'protractor';
 *
 *  const actor = Actor.named('Wendy').whoCan(
 *      BrowseTheWeb.using(protractor.browser),
 *  );
 *
 *  const HomePage = {
 *      Title: Target.the('title').located(by.css('h1')),
 *  };
 *
 *  actor.attemptsTo(
 *      Navigate.to(`https://serenity-js.org`),
 *      Ensure.that(Text.of(HomePage.Title), equals('Serenity/JS')),
 *  );
 *
 * @see https://www.protractortest.org/
 *
 * @public
 * @implements {@serenity-js/core/lib/screenplay~Ability}
 * @see {@link @serenity-js/core/lib/screenplay/actor~Actor}
 */
export class BrowseTheWeb implements Ability {

    /**
     * @private
     */
    private lastScriptExecutionSummary: LastScriptExecutionSummary;

    /**
     * @private
     */
    private originalWindowHandle: string;

    /**
     * @desc
     *  Ability to interact with web front-ends using a given protractor browser instance.
     *
     * @param {ProtractorBrowser} browser
     * @returns {BrowseTheWeb}
     */
    static using(browser: ProtractorBrowser): BrowseTheWeb {
        return new BrowseTheWeb(browser);
    }

    /**
     * @desc
     *  Used to access the Actor's ability to {@link BrowseTheWeb} from within the {@link Interaction} classes,
     *  such as {@link Navigate}.
     *
     * @param {UsesAbilities} actor
     * @return {BrowseTheWeb}
     */
    static as(actor: UsesAbilities): BrowseTheWeb {
        return actor.abilityTo(BrowseTheWeb);
    }

    /**
     * @param {ProtractorBrowser} browser
     *  An instance of a protractor browser
     */
    constructor(protected browser: ProtractorBrowser) {
    }

    /**
     * @desc
     *  Navigate to the given destination and loads mock modules before Angular.
     *  Assumes that the page being loaded uses Angular.
     *
     * @param {string} destination
     * @param {number?} timeoutInMillis
     *
     * @returns {Promise<void>}
     */
    get(destination: string, timeoutInMillis?: number): Promise<void> {
        return promiseOf(this.browser.get(destination, timeoutInMillis)
            .then(() => this.browser.getWindowHandle())
            .then(handle => {
                this.originalWindowHandle = handle;
            }),
        );
    }

    /**
     * @desc
     *  Interface for navigating back and forth in the browser history.
     *
     *  @returns {Navigation}
     */
    navigate(): Navigation {
        return this.browser.navigate();
    }

    /**
     * @desc
     *  Interface for defining sequences of complex user interactions.
     *  Each sequence will not be executed until `perform` is called.
     *
     * @returns {ActionSequence}
     *
     * @see https://www.selenium.dev/selenium/docs/api/javascript/module/selenium-webdriver/lib/actions.html
     */
    actions(): ActionSequence {
        return this.browser.actions();
    }

    /**
     * @desc
     *  Interface for managing browser and driver state.
     *
     * @returns {Options}
     *
     * @see https://www.selenium.dev/selenium/docs/api/javascript/module/selenium-webdriver/lib/webdriver_exports_WebDriver.html#manage
     */
    manage(): Options {
        /*
        this.browser.manage().deleteCookie();
        this.browser.manage().deleteAllCookies();
        return this.browser.manage().getCookie('asd');
         */

        return this.browser.manage();
    }

    /**
     * @desc
     *  Changes focus to the active modal dialog,
     *  such as those opened by
     *  [`Window.alert()`](https://developer.mozilla.org/en-US/docs/Web/API/Window/alert),
     *  [`Window.prompt()`](https://developer.mozilla.org/en-US/docs/Web/API/Window/prompt), or
     *  [`Window.confirm()`](https://developer.mozilla.org/en-US/docs/Web/API/Window/confirm).
     *
     * The returned promise will be rejected with an [`error.NoSuchAlertError`](https://www.selenium.dev/selenium/docs/api/javascript/module/selenium-webdriver/lib/error_exports_NoSuchAlertError.html)
     * if there are no open alerts.
     *
     * @returns {AlertPromise}
     *
     * @see https://www.selenium.dev/selenium/docs/api/javascript/module/selenium-webdriver/lib/webdriver_exports_TargetLocator.html#alert
     */
    alert(): AlertPromise {
        return this.browser.switchTo().alert();
    }

    /**
     * @desc
     *  Switches the focus to a [`frame`](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/frame) or
     *  [`iframe`](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/iframe) identified by `elementOrIndexOrName`,
     *  which can be specified either as {@link selenium-webdriver~WebElement}, the name of the frame, or its index.
     *
     * @param {number | string | WebElement} elementOrIndexOrName
     *
     * @returns {Promise<void>}
     */
    switchToFrame(elementOrIndexOrName: number | string | WebElement | WebElementPromise): Promise<void> {
        // incorrect type definition in selenium-webdriver prevents us from providing a string arg
        return promiseOf(this.browser.switchTo().frame(elementOrIndexOrName as any));
    }

    /**
     * @desc
     *  Switches the focus from any [`frame`](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/frame) or
     *  [`iframe`](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/iframe) back to its parent iframe.
     *
     * @returns {Promise<void>}
     */
    switchToParentFrame(): Promise<void> {
        return promiseOf(this.browser.driver.switchToParentFrame());
    }

    /**
     * @desc
     *  Switches the focus from any [`frame`](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/frame)
     *  or [`iframe`](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/iframe) back to default content,
     *  a.k.a. "the main window".
     *
     * @returns {Promise<void>}
     */
    switchToDefaultContent(): Promise<void> {
        return promiseOf(this.browser.switchTo().defaultContent());
    }

    /**
     * @desc
     *  Switches browser window/tab to the one identified by `nameOrHandleOrIndex`,
     *  which can be specified as the name of the window to switch to, its handle, or numeric index.
     *
     * @param {string | number} nameOrHandleOrIndex
     *
     * @returns {Promise<void>}
     */
    switchToWindow(nameOrHandleOrIndex: string | number): Promise<void> {
        return typeof nameOrHandleOrIndex === 'string'
            ? this.switchToWindowByNameOrHandle(nameOrHandleOrIndex)
            : this.switchToWindowByIndex(nameOrHandleOrIndex);
    }

    /**
     * @param {string} nameOrHandle
     * @private
     */
    private switchToWindowByNameOrHandle(nameOrHandle: string): Promise<void> {
        return promiseOf(this.browser.switchTo().window(nameOrHandle));
    }

    /**
     * @param {number} index
     * @private
     */
    private switchToWindowByIndex(index: number): Promise<void> {
        return promiseOf(this.browser.getAllWindowHandles().then(handles => {
            const handle = handles[index];

            if (! handle) {
                throw new LogicError(`Window ${ index } doesn't exist`)
            }

            return this.browser.switchTo().window(handle);
        }));
    }

    /**
     * @desc
     *  Switches the window back to the original one that was used to call {@link get}.
     *
     * @returns {Promise<void>}
     */
    switchToOriginalWindow(): Promise<void> {
        return this.originalWindowHandle
            ? promiseOf(this.browser.switchTo().window(this.originalWindowHandle))
            : Promise.resolve();
    }

    /**
     * @desc
     *  Returns the handle of the browser window last used to navigate to a URL.
     *
     * @returns {Promise<string>}
     *  A window handle
     *
     * @see {@link get}
     */
    getOriginalWindowHandle(): Promise<string> {
        return Promise.resolve(this.originalWindowHandle);
    }

    /**
     * @desc
     *  Returns the current window handle.
     *  Please note that the current handle changes with each browser window you {@link Switch} to.
     *
     * @returns {Promise<string>}
     *  A window handle
     *
     * @see {@link get}
     */
    getCurrentWindowHandle(): Promise<string> {
        return promiseOf(this.browser.getWindowHandle());
    }

    /**
     * @desc
     *  Returns the handles of all the available windows.
     *
     *  Please note that while some browsers organise entries of this list in the same order
     *  new windows have been spawned, other browsers order it alphabetically.
     *  For this reason, you should not make any assumptions about how this list is ordered.
     *
     * @returns {Promise<string[]>}
     *  A list of window handles
     */
    getAllWindowHandles(): Promise<string[]> {
        return promiseOf(this.browser.getAllWindowHandles());
    }

    /**
     * @desc
     *  Closes the currently active browser window/tab.
     *
     * @returns {Promise<void>}
     */
    closeCurrentWindow(): Promise<void> {
        return promiseOf(this.browser.close());
    }

    /**
     * @desc
     *  Locates a single element identified by the locator
     *
     * @param {Locator} locator
     * @returns {ElementFinder}
     */
    locate(locator: Locator): ElementFinder {
        return this.browser.element(locator);
    }

    /**
     * @desc
     *  Locates all elements identified by the locator
     *
     * @param {Locator} locator
     * @returns {ElementArrayFinder}
     */
    locateAll(locator: Locator): ElementArrayFinder {
        return this.browser.element.all(locator);
    }

    /**
     * @desc
     * If set to false, Protractor will not wait for Angular $http and $timeout
     * tasks to complete before interacting with the browser.
     *
     * This can be useful when:
     * - you need to switch to a non-Angular app during your tests (i.e. SSO login gateway)
     * - your app continuously polls an API with $timeout
     *
     * If you're not testing an Angular app, it's better to disable Angular synchronisation completely
     * in protractor configuration:
     *
     * @example <caption>protractor.conf.js</caption>
     * exports.config = {
     *     onPrepare: function () {
     *         return browser.waitForAngularEnabled(false);
     *     },
     *
     *     // ... other config
     * };
     *
     * @param {boolean} enable
     *
     * @returns {Promise<boolean>}
     */
    enableAngularSynchronisation(enable: boolean): Promise<boolean> {
        return promiseOf(this.browser.waitForAngularEnabled(enable));
    }

    /**
     * @desc
     *  Schedules a command to execute JavaScript in the context of the currently selected frame or window.
     *  The script fragment will be executed as the body of an anonymous function.
     *  If the script is provided as a function object, that function will be converted to a string for injection
     *  into the target window.
     *
     *  Any arguments provided in addition to the script will be included as script arguments and may be referenced
     *  using the `arguments` object. Arguments may be a `boolean`, `number`, `string` or `WebElement`.
     *  Arrays and objects may also be used as script arguments as long as each item adheres
     *  to the types previously mentioned.
     *
     *  The script may refer to any variables accessible from the current window.
     *  Furthermore, the script will execute in the window's context, thus `document` may be used to refer
     *  to the current document. Any local variables will not be available once the script has finished executing,
     *  though global variables will persist.
     *
     *  If the script has a return value (i.e. if the script contains a `return` statement),
     *  then the following steps will be taken for resolving this functions return value:
     *
     *  For a HTML element, the value will resolve to a WebElement
     *  - Null and undefined return values will resolve to null
     *  - Booleans, numbers, and strings will resolve as is
     *  - Functions will resolve to their string representation
     *  - For arrays and objects, each member item will be converted according to the rules above
     *
     * @example <caption>Perform a sleep in the browser under test</caption>
     * BrowseTheWeb.as(actor).executeAsyncScript(`
     *   return arguments[0].tagName;
     * `, Target.the('header').located(by.css(h1))
     *
     * @see https://www.protractortest.org/#/api?view=webdriver.WebDriver.prototype.executeScript
     * @see https://seleniumhq.github.io/selenium/docs/api/java/org/openqa/selenium/JavascriptExecutor.html#executeScript-java.lang.String-java.lang.Object...-
     *
     * @param {string} description  - useful for debugging
     * @param {string | Function} script
     * @param {any[]} args
     *
     * @returns {Promise<any>}
     *
     * @see {@link BrowseTheWeb#getLastScriptExecutionResult}
     */
    executeScript(description: string, script: string | Function, ...args: any[]): Promise<any> {   // eslint-disable-line @typescript-eslint/ban-types
        return promiseOf(this.browser.executeScriptWithDescription(script, description, ...args))
            .then(result => {
                this.lastScriptExecutionSummary = new LastScriptExecutionSummary(
                    result,
                );
                return result;
            });
    }

    /**
     * @desc
     *  A simplified version of {@link BrowseTheWeb#executeScript} that doesn't affect {@link LastScriptExecution.result()}.
     *
     * @param {Function} fn
     * @param {Parameters<fn>} args
     *
     * @returns {Promise<ReturnType<fn>>}
     */
    executeFunction<F extends (...args: any[]) => any>(fn: F, ...args: Parameters<F>): Promise<ReturnType<F>> {
        return promiseOf(this.browser.executeScriptWithDescription(fn, fn.name, ...args));
    }

    /**
     * @desc
     *  Schedules a command to execute asynchronous JavaScript in the context of the currently selected frame or window.
     *  The script fragment will be executed as the body of an anonymous function.
     *  If the script is provided as a function object, that function will be converted to a string for injection
     *  into the target window.
     *
     *  Any arguments provided in addition to the script will be included as script arguments and may be referenced
     *  using the `arguments` object. Arguments may be a `boolean`, `number`, `string` or `WebElement`
     *  Arrays and objects may also be used as script arguments as long as each item adheres
     *  to the types previously mentioned.
     *
     *  Unlike executing synchronous JavaScript with {@link BrowseTheWeb#executeScript},
     *  scripts executed with this function must explicitly signal they are finished by invoking the provided callback.
     *
     *  This callback will always be injected into the executed function as the last argument,
     *  and thus may be referenced with `arguments[arguments.length - 1]`.
     *
     *  The following steps will be taken for resolving this functions return value against
     *  the first argument to the script's callback function:
     *
     *  - For a HTML element, the value will resolve to a WebElement
     *  - Null and undefined return values will resolve to null
     *  - Booleans, numbers, and strings will resolve as is
     *  - Functions will resolve to their string representation
     *  - For arrays and objects, each member item will be converted according to the rules above
     *
     * @example <caption>Perform a sleep in the browser under test</caption>
     * BrowseTheWeb.as(actor).executeAsyncScript(`
     *   var delay    = arguments[0];
     *   var callback = arguments[arguments.length - 1];
     *
     *   window.setTimeout(callback, delay);
     * `, 500)
     *
     * @example <caption>Return a value asynchronously</caption>
     * BrowseTheWeb.as(actor).executeAsyncScript(`
     *   var callback = arguments[arguments.length - 1];
     *
     *   callback('some return value')
     * `).then(value => doSomethingWithThe(value))
     *
     * @see https://www.protractortest.org/#/api?view=webdriver.WebDriver.prototype.executeAsyncScript
     * @see https://seleniumhq.github.io/selenium/docs/api/java/org/openqa/selenium/JavascriptExecutor.html#executeAsyncScript-java.lang.String-java.lang.Object...-
     *
     * @param {string|Function} script
     * @param {any[]} args
     *
     * @returns {Promise<any>}
     *
     * @see {@link BrowseTheWeb#getLastScriptExecutionResult}
     */
    executeAsyncScript(script: string | Function, ...args: any[]): Promise<any> {   // eslint-disable-line @typescript-eslint/ban-types
        return promiseOf(this.browser.executeAsyncScript(script, ...args))
            .then(result => {
                this.lastScriptExecutionSummary = new LastScriptExecutionSummary(
                    result,
                );
                return result;
            });
        // todo: should I wrap this an provide additional diagnostic information? execution time? error handling?
    }

    /**
     * @desc
     *  Schedule a command to take a screenshot. The driver makes a best effort to
     *  return a base64-encoded screenshot of the following, in order of preference:
     *
     *  1. Entire page
     *  2. Current window
     *  3. Visible portion of the current frame
     *  4. The entire display containing the browser
     *
     * @return {Promise<string>} A promise that will be resolved to a base64-encoded screenshot PNG
     */
    takeScreenshot(): Promise<string> {
        return promiseOf(this.browser.takeScreenshot());
    }

    /**
     * @desc
     *  Returns the title of the current page.
     *
     * @see https://developer.mozilla.org/en-US/docs/Web/HTML/Element/title
     *
     * @returns {Promise<string>}
     */
    getTitle(): Promise<string> {
        return promiseOf(this.browser.getTitle());
    }

    /**
     * @desc
     *  Returns the url of the current page.
     *
     * @returns {Promise<string>}
     */
    getCurrentUrl(): Promise<string> {
        return promiseOf(this.browser.getCurrentUrl());
    }

    /**
     * @desc
     *  Returns the  capabilities of the browser used in the current session.
     *
     *  By default, the session `capabilities` specified in the `protractor.conf.js`
     *  indicate the _desired_ properties of the remote browser. However, if the remote cannot satisfy
     *  all the requirements, it will still create a session.
     *
     * @returns {Promise<Capabilities>} The actual capabilities of this browser.
     */
    getCapabilities(): Promise<Capabilities> {
        return promiseOf(this.browser.getCapabilities());
    }

    /**
     * @desc
     *  Pause the actor flow for a specified number of milliseconds.
     *
     * @param {number} millis
     * @returns {Promise<void>}
     */
    sleep(millis: number): Promise<void> {
        return promiseOf(this.browser.sleep(millis));
    }

    /**
     * @desc
     *  Pause the actor flow until the condition is met or the timeout expires.
     *
     * @param {function(): Promise<boolean>} condition
     * @param {number} timeoutInMillis
     * @returns {Promise<boolean>}
     */
    wait(condition: () => Promise<boolean>, timeoutInMillis: number): Promise<boolean> {
        return promiseOf(this.browser.wait(condition, timeoutInMillis));
    }

    /**
     * @desc
     *  Returns the last result of calling {@link BrowseTheWeb#executeAsyncScript}
     *  or {@link BrowseTheWeb#executeScript}
     *
     * @returns {any}
     */
    getLastScriptExecutionResult(): any {
        if (! this.lastScriptExecutionSummary) {
            throw new LogicError(`Make sure to execute a script before checking on the result`);
        }

        return this.lastScriptExecutionSummary.result;
    }

    /**
     * @desc
     *  Returns Protractor configuration parameter at `path`.
     *
     * @example <caption>protractor.conf.js</caption>
     *  exports.config = {
     *    params: {
     *        login: {
     *            username: 'jane@example.org'
     *            password: process.env.PASSWORD
     *        }
     *    }
     *    // ...
     * }
     *
     * @example <caption>Retrieving config param by name</caption>
     *  BrowseTheWeb.as(actor).param('login') // returns object with username and password
     *
     * @example <caption>Retrieving config param by path</caption>
     *  BrowseTheWeb.as(actor).param('login.username') // returns string 'jane@example.org'
     *
     * @param {string} path
     *  Either a name or a dot-delimited path to the param.
     *
     * @returns {T}
     *
     * @throws {@serenity-js/core/lib/errors~ConfigurationError}
     *  Throws a `ConfigurationError` if the parameter is `undefined`
     */
    param<T = any>(path: string): T {
        return path.split('.')
            .reduce((config, segment) => {
                if (! (config && config[segment] !== undefined)) {
                    throw new ConfigurationError(`Protractor param '${ path }' is undefined`);
                }

                return config[segment];
            }, this.browser.params);
    }
}

/**
 * @package
 */
class LastScriptExecutionSummary {
    constructor(public readonly result: any) {}
}

export * from './abilities';
export * from './interactions';
export * from './questions';

import { AnswersQuestions, Interaction, Question, UsesAbilities } from '@serenity-js/core';
import { formatted } from '@serenity-js/core/lib/io';
import { AlertPromise } from 'selenium-webdriver';

/**
 * @desc
 *  Instructs the {@link @serenity-js/core/lib/screenplay/actor~Actor} to accept
 *  a {@link ModalDialog}.
 *
 * @example <caption>Example widget</caption>
 *  <button
 *      data-test="trigger"
 *      onclick="alert('hello!')">Trigger Alert</button>
 *
 * @example <caption>Lean Page Object</caption>
 *  import { Target } from '@serenity-js/protractor';
 *  import { by } from 'protractor';
 *
 *  class Widget {
 *      static trigger = Target.the('trigger button')
 *          .located(by.css('[data-test="trigger"]'));
 *  }
 *
 * @example <caption>Accepting a modal dialog window</caption>
 *  import { actorCalled } from '@serenity-js/core';
 *  import { Accept, BrowseTheWeb, Click, ModalDialog } from '@serenity-js/protractor';
 *  import { protractor } from 'protractor';
 *
 *  actorCalled('Nick')
 *      .whoCan(BrowseTheWeb.using(protractor.browser))
 *      .attemptsTo(
 *          Click.on(Widget.trigger),
 *          Accept.the(ModalDialog.window()),
 *      );
 *
 * @extends {@serenity-js/core/lib/screenplay~Interaction}
 *
 * @see {@link BrowseTheWeb}
 * @see {@link Click}
 * @see {@link Dismiss}
 * @see {@link ModalDialog}
 */
export class Accept extends Interaction {

    /**
     * @desc
     *  Instantiates this {@link @serenity-js/core/lib/screenplay~Interaction}
     *  with a {@link ModalDialog.window} the {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  will accept.
     *
     * @param {@serenity-js/core/lib/screenplay~Question<AlertPromise> | AlertPromise} modalDialogWindow
     * @returns {@serenity-js/core/lib/screenplay~Interaction}
     *
     * @see {@link @serenity-js/core/lib/screenplay~Question}
     */
    static the(modalDialogWindow: Question<AlertPromise> | AlertPromise): Interaction {
        return new Accept(modalDialogWindow);
    }

    /**
     * @param {@serenity-js/core/lib/screenplay~Question<AlertPromise> | AlertPromise} modalDialogWindow
     */
    constructor(private readonly modalDialogWindow: Question<AlertPromise> | AlertPromise) {
        super();
    }

    /**
     * @desc
     *  Makes the provided {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  perform this {@link @serenity-js/core/lib/screenplay~Interaction}.
     *
     * @param {UsesAbilities & AnswersQuestions} actor
     *  An {@link @serenity-js/core/lib/screenplay/actor~Actor} to perform this {@link @serenity-js/core/lib/screenplay~Interaction}
     *
     * @returns {PromiseLike<void>}
     *
     * @see {@link @serenity-js/core/lib/screenplay/actor~Actor}
     * @see {@link @serenity-js/core/lib/screenplay/actor~UsesAbilities}
     * @see {@link @serenity-js/core/lib/screenplay/actor~AnswersQuestions}
     */
    performAs(actor: UsesAbilities & AnswersQuestions): PromiseLike<void> {
        return actor.answer(this.modalDialogWindow)
            .then(alert => alert.accept());
    }

    /**
     * @desc
     *  Generates a description to be used when reporting this {@link @serenity-js/core/lib/screenplay~Activity}.
     *
     * @returns {string}
     */
    toString(): string {
        return formatted `#actor accepts ${ this.modalDialogWindow }`;
    }
}

