import { Question } from '@serenity-js/core';
import { Interaction } from '@serenity-js/core/lib/screenplay';
import { ElementFinder } from 'protractor';

/**
 * @desc
 *  Configures the {@link Target} representing
 *  a [HTML `<select>` element](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/select)
 *  for the {@link @serenity-js/core/lib/screenplay/actor~Actor}
 *  to interact with.
 *
 * @interface
 */
export interface SelectBuilder {

    /**
     * @desc
     *  Configures the {@link Target} representing
     *  a [HTML `<select>` element](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/select)
     *  for the {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  to interact with
     *
     * @param {Question<ElementFinder> | ElementFinder} target
     *
     * @returns {@serenity-js/core/lib/screenplay~Interaction}
     *
     * @see {@link Select}
     * @see {@link Select.option}
     * @see {@link Select.options}
     * @see {@link Select.value}
     * @see {@link Select.values}
     * @see {@link Target}
     */
    from: (target: Question<ElementFinder> | ElementFinder) => Interaction;
}

import { Activity, Answerable, AnswersQuestions, Interaction, LogicError, PerformsActivities, Task, UsesAbilities } from '@serenity-js/core';
import { ElementFinder } from 'protractor';
import { WebElementPromise } from 'selenium-webdriver';

import { BrowseTheWeb } from '../abilities';

/**
 * @desc
 *  Instructs the {@link @serenity-js/core/lib/screenplay/actor~Actor}
 *  to switch to a different [frame](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/frame),
 *  [inline frame](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/iframe),
 *  or browser window/tab.
 *
 * @example <caption>Lean Page Object describing a login form, embedded in an iframe</caption>
 *
 *  import { Target } from '@serenity-js/protractor';
 *  import { by } from 'protractor';
 *
 *  class LoginForm {
 *      static iframe           = Target.the('login form').located(by.tagName('iframe'));
 *      static usernameField    = Target.the('username field').located(by.css('[data-test="username"]'));
 *      static passwordField    = Target.the('password field').located(by.css('[data-test="password"]'));
 *      static submitButton     = Target.the('submit button').located(by.css(`button[type='submit']`));
 *  }
 *
 * @example <caption>Switch to an iframe and back</caption>
 *
 *  import { actorCalled } from '@serenity-js/core';
 *  import { BrowseTheWeb, Switch, Enter, Click } from '@serenity-js/protractor';
 *  import { protractor } from 'protractor';
 *
 *  actorCalled('Francesca')
 *      .whoCan(BrowseTheWeb.using(protractor.browser))
 *      .attemptsTo(
 *          Switch.toFrame(LoginForm.iframe),
 *
 *          Enter.theValue('francesca@example.org').into(LoginForm.usernameField),
 *          Enter.theValue('correct-horse-battery-staple').into(LoginForm.passwordField),
 *          Click.on(LoginForm.submitButton),
 *
 *          Switch.toParentFrame(),
 *      );
 *
 * @example <caption>Perform activities in the context of an iframe</caption>
 *
 *  import { actorCalled } from '@serenity-js/core';
 *  import { BrowseTheWeb, Switch, Enter, Click } from '@serenity-js/protractor';
 *  import { protractor } from 'protractor';
 *
 *  actorCalled('Francesca')
 *      .whoCan(BrowseTheWeb.using(protractor.browser))
 *      .attemptsTo(
 *          Switch.toFrame(LoginForm.iframe).and(
 *              Enter.theValue('francesca@example.org').into(LoginForm.usernameField),
 *              Enter.theValue('correct-horse-battery-staple').into(LoginForm.passwordField),
 *              Click.on(LoginForm.submitButton),
 *          ),
 *          // Note that Switch.toParentFrame() is invoked automatically
 *      );
 *
 * @example <caption>Switch to a new window/tab and back</caption>
 *
 *  import { actorCalled } from '@serenity-js/core';
 *  import { BrowseTheWeb, Switch, Close } from '@serenity-js/protractor';
 *  import { protractor } from 'protractor';
 *
 *  actorCalled('Francesca')
 *      .whoCan(BrowseTheWeb.using(protractor.browser))
 *      .attemptsTo(
 *          Switch.toNewWindow(), // or: Switch.toWindow(...)
 *
 *          // perform some activities in the context of the new window
 *
 *          Close.currentWindow(),
 *
 *          Switch.toOriginalWindow(),
 *      );
 *
 * @example <caption>Perform activities in the context of a different window/tab</caption>
 *
 *  import { actorCalled } from '@serenity-js/core';
 *  import { BrowseTheWeb, Switch, Close } from '@serenity-js/protractor';
 *  import { protractor } from 'protractor';
 *
 *  actorCalled('Francesca')
 *      .whoCan(BrowseTheWeb.using(protractor.browser))
 *      .attemptsTo(
 *          Switch.toNewWindow().and(
 *              // perform some activities in the context of the new window
 *
 *              Close.currentWindow()
 *          ),
 *
 *          // Note that Switch.toOriginalWindow() is invoked automatically
 *      );
 *
 * @see {@link Close}
 * @see {@link BrowseTheWeb}
 */
export class Switch {

    /**
     * @desc
     *  Switches the current [browsing context](https://w3c.github.io/webdriver/#dfn-current-browsing-context)
     *  for future commands to a [frame](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/frame)
     *  or an [inline frame](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/iframe)
     *  identified by its name, index or `Question<ElementFinder>`.
     *
     * @param {Answerable<ElementFinder | number | string>} targetOrIndex
     *
     * @returns {SwitchToFrame}
     *
     * @see {@link Switch.toParentFrame}
     * @see {@link Switch.toDefaultContent}
     * @see {@link Target}
     */
    static toFrame(targetOrIndex: Answerable<ElementFinder | number | string>): SwitchToFrame {
        return new SwitchToFrame(targetOrIndex);
    }

    /**
     * @desc
     *  Sets the current [browsing context](https://w3c.github.io/webdriver/#dfn-current-browsing-context)
     *  for future commands to the parent of the current browsing context,
     *  i.e. an `iframe` in which the current `iframe` is nested.
     *
     *  If the current context is the top-level browsing context, the context remains unchanged.
     *
     * @returns {@serenity-js/core/lib/screenplay~Interaction}
     *
     * @see {@link Switch.toFrame}
     * @see https://w3c.github.io/webdriver/#switch-to-parent-frame
     * @see https://w3c.github.io/webdriver/#dfn-current-browsing-context
     */
    static toParentFrame(): Interaction {
        return new SwitchToParentFrame();
    }

    /**
     * @desc
     *  Switches the current [browsing context](https://w3c.github.io/webdriver/#dfn-current-browsing-context)
     *  for future commands to the first frame on the page, or the main document
     *  when a page contains [`iframe`](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/iframe)s.
     *
     * @returns {@serenity-js/core/lib/screenplay~Interaction}
     *
     * @see {@link Switch.toFrame}
     */
    static toDefaultContent(): Interaction {
        return new SwitchToDefaultContent();
    }

    /**
     * @desc
     *  Switches the current [browsing context](https://w3c.github.io/webdriver/#dfn-current-browsing-context)
     *  for future commands to a browser tab/window identified by its
     *  name, index or [window handle](https://developer.mozilla.org/en-US/docs/Web/WebDriver/Commands/GetWindowHandles).
     *
     * @param {Answerable<string | number>} nameOrHandleOrIndex
     * @returns {SwitchToWindow}
     */
    static toWindow(nameOrHandleOrIndex: Answerable<string | number>): SwitchToWindow {
        return new SwitchToWindow(nameOrHandleOrIndex);
    }

    /**
     * @desc
     *  Switches the current [browsing context](https://w3c.github.io/webdriver/#dfn-current-browsing-context)
     *  for future commands to the most recently opened browser tab/window.
     *
     *  **Please note** that this behaviour might vary in some browsers if there are more than two windows opened
     *  at the same time.
     *
     * @returns {SwitchToNewWindow}
     */
    static toNewWindow(): SwitchToNewWindow {
        return new SwitchToNewWindow();
    }

    /**
     * @desc
     *  Switches the current [browsing context](https://w3c.github.io/webdriver/#dfn-current-browsing-context)
     *  for future commands to the original window used when the {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  performed an interaction to {@link Navigate}.
     *
     *  **Please note** that this behaviour might vary in some browsers if there are more than two windows opened
     *  at the same time, as window handles might be ordered alphabetically instead of the order in which they were created.
     *
     * @returns {SwitchToOriginalWindow}
     */
    static toOriginalWindow(): SwitchToOriginalWindow {
        return new SwitchToOriginalWindow();
    }
}

/**
 * @package
 */
class SwitchToFrame extends Interaction {
    constructor(private readonly targetOrIndex: Answerable<ElementFinder | number | string>) {
        super();
    }

    and(...activities: Activity[]): Task {
        return new SwitchToFrameAndPerformActivities(this.targetOrIndex, activities);
    }

    performAs(actor: UsesAbilities & AnswersQuestions): PromiseLike<void> {
        return actor.answer(this.targetOrIndex)
            .then((targetOrIndex: ElementFinder) =>
                BrowseTheWeb.as(actor).switchToFrame(
                    targetOrIndex instanceof ElementFinder
                        ? targetOrIndex.getWebElement() as unknown as WebElementPromise // https://github.com/angular/protractor/issues/1846#issuecomment-82634739
                        : targetOrIndex,
                ),
            );
    }

    toString(): string {
        return `#actor switches to frame: ${ this.targetOrIndex }`;
    }
}

/**
 * @package
 */
class SwitchToFrameAndPerformActivities extends Task {
    constructor(
        private readonly targetOrIndex: Answerable<ElementFinder | number | string>,
        private readonly activities: Activity[]
    ) {
        super();
    }

    performAs(actor: PerformsActivities): PromiseLike<void> {
        return actor.attemptsTo(
            new SwitchToFrame(this.targetOrIndex),
            ...this.activities,
            new SwitchToParentFrame()
        )
    }

    toString(): string {
        return `#actor switches to frame: ${ this.targetOrIndex }`;
    }
}

/**
 * @package
 */
class SwitchToParentFrame extends Interaction {
    performAs(actor: UsesAbilities & AnswersQuestions): PromiseLike<void> {
        return BrowseTheWeb.as(actor).switchToParentFrame();
    }

    toString(): string {
        return `#actor switches to parent frame`;
    }
}

/**
 * @package
 */
class SwitchToDefaultContent extends Interaction {
    performAs(actor: UsesAbilities & AnswersQuestions): PromiseLike<void> {
        return BrowseTheWeb.as(actor).switchToDefaultContent();
    }

    toString(): string {
        return `#actor switches to default content`;
    }
}

/**
 * @package
 */
class SwitchToWindow extends Interaction {
    constructor(private readonly nameOrHandleOrIndex: Answerable<string | number>) {
        super();
    }

    and(...activities: Activity[]): Task {
        return new SwitchToWindowAndPerformActivities(this.nameOrHandleOrIndex, activities);
    }

    performAs(actor: UsesAbilities & AnswersQuestions): PromiseLike<void> {
        return actor.answer(this.nameOrHandleOrIndex).then(index => BrowseTheWeb.as(actor).switchToWindow(index));
    }

    toString(): string {
        return `#actor switches to window: ${this.nameOrHandleOrIndex}`;
    }
}

/**
 * @package
 */
class SwitchToWindowAndPerformActivities extends Task {
    constructor(
        private readonly nameOrHandleOrIndex: Answerable<string | number>,
        private readonly activities: Activity[],
    ) {
        super();
    }

    performAs(actor: PerformsActivities & UsesAbilities): PromiseLike<void> {
        return BrowseTheWeb.as(actor).getCurrentWindowHandle()
            .then(currentWindow => {
                return actor.attemptsTo(
                    new SwitchToWindow(this.nameOrHandleOrIndex),
                    ...this.activities,
                    new SwitchToWindow(currentWindow),
                );
            });
    }

    toString(): string {
        return `#actor switches to window: ${ this.nameOrHandleOrIndex }`;
    }
}

/**
 * @package
 */
class SwitchToNewWindow extends Interaction {
    constructor() {
        super();
    }

    and(...activities: Activity[]): Task {
        return new SwitchToNewWindowAndPerformActivities(activities);
    }

    performAs(actor: UsesAbilities & AnswersQuestions): PromiseLike<void> {
        return Promise.all([
            BrowseTheWeb.as(actor).getCurrentWindowHandle(),
            BrowseTheWeb.as(actor).getAllWindowHandles(),
        ])
        .then(([ currentHandle, handles ]: [ string, string[] ]) => handles.filter(handle => handle !== currentHandle))
        .then(handles => {
            if (handles.length === 0) {
                throw new LogicError(`No new window has been opened to switch to`)
            }

            return BrowseTheWeb.as(actor).switchToWindow(handles[handles.length - 1]);
        });
    }

    toString(): string {
        return `#actor switches to the new browser window`;
    }
}

/**
 * @package
 */
class SwitchToNewWindowAndPerformActivities extends Task {
    constructor(private readonly activities: Activity[]) {
        super();
    }

    performAs(actor: PerformsActivities & UsesAbilities): PromiseLike<void> {
        return BrowseTheWeb.as(actor).getCurrentWindowHandle()
            .then(currentWindow => {
                return actor.attemptsTo(
                    new SwitchToNewWindow(),
                    ...this.activities,
                    new SwitchToWindow(currentWindow),
                );
            });
    }

    toString(): string {
        return `#actor switches to the new window`;
    }
}

/**
 * @package
 */
class SwitchToOriginalWindow extends Interaction {
    constructor() {
        super();
    }

    performAs(actor: UsesAbilities & AnswersQuestions): PromiseLike<void> {
        return BrowseTheWeb.as(actor).switchToOriginalWindow();
    }

    toString(): string {
        return `#actor switches back to the original browser window`;
    }
}

import { Answerable, AnswersQuestions, CollectsArtifacts, Interaction, UsesAbilities } from '@serenity-js/core';
import { formatted } from '@serenity-js/core/lib/io';
import { Name, Photo } from '@serenity-js/core/lib/model';

import { BrowseTheWeb } from '../abilities';

/**
 * @desc
 *  Instructs the {@link @serenity-js/core/lib/screenplay/actor~Actor} to
 *  take a screenshot and emit an {@link @serenity-js/core/lib/model~Artifact},
 *  which can then be persisted by {@link @serenity-js/core/lib/stage/crew/artifact-archiver~ArtifactArchiver}
 *  and reported by [Serenity BDD reporter](/modules/serenity-bdd).
 *
 * @example <caption>Clicking on an element</caption>
 *  import { actorCalled } from '@serenity-js/core';
 *  import { BrowseTheWeb, Navigate, TakeScreenshot } from '@serenity-js/protractor';
 *  import { protractor } from 'protractor';
 *
 *  actorCalled('Tania')
 *      .whoCan(BrowseTheWeb.using(protractor.browser))
 *      .attemptsTo(
 *          Navigate.to('/app'),
 *          TakeScreenshot.of('my app'),
 *      );
 *
 * @see {@link BrowseTheWeb}
 * @see {@link @serenity-js/core/lib/screenplay/actor~CollectsArtifacts}
 * @see {@link @serenity-js/core/lib/model~Artifact}
 * @see {@link @serenity-js/core/lib/stage/crew/artifact-archiver~ArtifactArchiver}
 *
 * @extends {@serenity-js/core/lib/screenplay~Interaction}
 */
export class TakeScreenshot extends Interaction {

    /**
     * @desc
     *  Instantiates this {@link @serenity-js/core/lib/screenplay~Interaction}.
     *
     * @param {Answerable<string>} name
     *  The name to associate the screenshot with
     *
     * @returns {@serenity-js/core/lib/screenplay~Interaction}
     */
    static of(name: Answerable<string>): Interaction {
        return new TakeScreenshot(name);
    }

    /**
     * @param {Answerable<string>} name
     *  The name to associate the screenshot with
     */
    constructor(private readonly name: Answerable<string>) {
        super();
    }

    /**
     * @desc
     *  Makes the provided {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  perform this {@link @serenity-js/core/lib/screenplay~Interaction}.
     *
     * @param {UsesAbilities & AnswersQuestions} actor
     *  An {@link @serenity-js/core/lib/screenplay/actor~Actor} to perform this {@link @serenity-js/core/lib/screenplay~Interaction}
     *
     * @returns {PromiseLike<void>}
     *
     * @see {@link @serenity-js/core/lib/screenplay/actor~Actor}
     * @see {@link @serenity-js/core/lib/screenplay/actor~UsesAbilities}
     * @see {@link @serenity-js/core/lib/screenplay/actor~AnswersQuestions}
     */
    performAs(actor: UsesAbilities & AnswersQuestions & CollectsArtifacts): PromiseLike<void> {
        return Promise.all([
            BrowseTheWeb.as(actor).takeScreenshot(),
            actor.answer(this.name),
        ]).then(([ screenshot, name ]) => actor.collect(
            Photo.fromBase64(screenshot),
            new Name(name),
        ));
    }

    /**
     * @desc
     *  Generates a description to be used when reporting this {@link @serenity-js/core/lib/screenplay~Activity}.
     *
     * @returns {string}
     */
    toString(): string {
        return formatted `#actor takes a screenshot of ${this.name}`;
    }
}

import { Interaction } from '@serenity-js/core';

import { BrowseTheWeb } from '../abilities';

/**
 * @desc
 *  Instructs the {@link @serenity-js/core/lib/screenplay/actor~Actor} to
 *  enable or disable automated synchronisation between Protractor and Angular.
 *
 *  Useful when a test scenario needs to interact with both Angular-based
 *  and non-Angular web apps (i.e. use an external sign on form).
 *
 *  More examples in [Cross-application testing with Serenity/JS](https://janmolak.com/cross-application-testing-with-serenity-js-4103a272b75b).
 *
 *  **Please note** if your tests interact with a non-Angular app you can disable
 *  synchronisation altogether in [`protractor.conf.js`](https://github.com/angular/protractor/blob/master/lib/config.ts).
 *
 * @example <caption>Disabling synchronisation temporarily</caption>
 *  import { actorCalled } from '@serenity-js/core';
 *  import { BrowseTheWeb, UseAngular, Navigate } from '@serenity-js/protractor';
 *  import { protractor } from 'protractor';
 *
 *  actorCalled('Angie')
 *      .whoCan(BrowseTheWeb.using(protractor.browser))
 *      .attemptsTo(
 *          UseAngular.disableSynchronisation(),
 *          Navigate.to('https://mycompany.com/login'),
 *          // navigate to a non-Angular app, perform some activities...
 *
 *          UseAngular.disableSynchronisation(),
 *          Navigate.to('https://myapp.com'),
 *          // navigate to an Angular app, perform some more activities...
 *      );
 *
 * @example <caption>Disabling synchronisation in protractor.conf.js</caption>
 *  exports.config = {
 *      onPrepare: function () {
 *          return browser.waitForAngularEnabled(false);
 *      },
 *      // ... other config
 *  };
 *
 * @extends {@serenity-js/core/lib/screenplay~Interaction}
 */
export class UseAngular {

    /**
     * @desc
     *  Instructs the {@link @serenity-js/core/lib/screenplay/actor~Actor} to
     *  disable synchronisation between Protractor and Angular.
     *
     * @returns {@serenity-js/core/lib/screenplay~Interaction}
     */
    static disableSynchronisation(): Interaction {
        return Interaction.where(`#actor disables synchronisation with Angular`, actor =>
            BrowseTheWeb.as(actor).enableAngularSynchronisation(false).then(() => void 0));
    }

    /**
     * @desc
     *  Instructs the {@link @serenity-js/core/lib/screenplay/actor~Actor} to
     *  enable synchronisation between Protractor and Angular.
     *
     * @returns {@serenity-js/core/lib/screenplay~Interaction}
     */
    static enableSynchronisation(): Interaction {
        return Interaction.where(`#actor enables synchronisation with Angular`, actor =>
            BrowseTheWeb.as(actor).enableAngularSynchronisation(true).then(() => void 0));
    }
}

import { Answerable, AnswersQuestions, AssertionError, Duration, Expectation, ExpectationMet, ExpectationOutcome, Interaction, UsesAbilities } from '@serenity-js/core';
import { formatted } from '@serenity-js/core/lib/io';

import { BrowseTheWeb } from '../abilities';
import { WaitBuilder } from './WaitBuilder';

/**
 * @desc
 *  Instructs the {@link @serenity-js/core/lib/screenplay/actor~Actor} to
 *  wait before proceeding either for a set {@link @serenity-js/core/lib/model~Duration}
 *  or until a given {@link @serenity-js/core/lib/screenplay/questions~Expectation} is met.
 *
 *  Useful when a test scenario can't take advantage of automatic synchronisation between
 *  Protractor and Angular (see {@link UseAngular}), or when the application under test is animation-heavy.
 *
 * @example <caption>Example widget</caption>
 *  <!--
 *      After about 1 second, the text will change from 'Loading...' to 'Ready!'
 *  -->
 *  <h1 id="status">Loading...</h1>
 *  <script>
 *      (function () {
 *          setTimeout(function () {
 *              document.getElementById('status').textContent = 'Ready!'
 *          }, 1000);
 *      })();
 *  </script>
 *
 * @example <caption>Lean Page Object describing the widget</caption>
 *  import { Target } from '@serenity-js/protractor';
 *  import { by } from 'protractor';
 *
 *  class App {
 *      static status = Target.the('status widget')
 *          .located(by.id('status'));
 *  }
 *
 * @example <caption>Waiting for a set amount of time</caption>
 *  import { actorCalled, Duration } from '@serenity-js/core';
 *  import { BrowseTheWeb, Wait } from '@serenity-js/protractor';
 *  import { Ensure, equals } from '@serenity-js/assertions';
 *  import { protractor } from 'protractor';
 *
 *  actorCalled('Aurora')
 *      .whoCan(BrowseTheWeb.using(protractor.browser))
 *      .attemptsTo(
 *          Wait.for(Duration.ofSeconds(1.5)),
 *          Ensure.that(App.status, equals('Ready!')),
 *      );
 *
 *  // Please note that while the above implementation works,
 *  // this approach is inefficient because at best
 *  // the actor might wait too long and at worst the test
 *  // might become "flaky" if any external interference
 *  // (like network glitches, animations taking a bit too long etc.)
 *  // makes the actor wait not long enough.
 *
 * @example <caption>Waiting until a condition is met</caption>
 *  import { actorCalled } from '@serenity-js/core';
 *  import { BrowseTheWeb, Wait, Text } from '@serenity-js/protractor';
 *  import { equals } from '@serenity-js/assertions';
 *  import { protractor } from 'protractor';
 *
 *  actorCalled('Aurora')
 *      .whoCan(BrowseTheWeb.using(protractor.browser))
 *      .attemptsTo(
 *          Wait.until(Text.of(App.status), equals('Ready!')),
 *          // app is ready, proceed with the scenario
 *      );
 *
 *  // Wait.until makes the Actor keep asking a Question,
 *  // in this case Text.of(App.status), until the answer meets
 *  // the expectation, or a timeout expires (default: 5s).
 *  //
 *  // Please note that both Ensure and Wait can be used with
 *  // the same expectations, like `equals`.
 *
 * @example <caption>Changing the default timeout</caption>
 *  import { actorCalled, Duration } from '@serenity-js/core';
 *  import { BrowseTheWeb, Wait, Text } from '@serenity-js/protractor';
 *  import { equals } from '@serenity-js/assertions';
 *  import { protractor } from 'protractor';
 *
 *  actorCalled('Aurora')
 *      .whoCan(BrowseTheWeb.using(protractor.browser))
 *      .attemptsTo(
 *          Wait.upTo(Duration.ofSeconds(3))
 *              .until(Text.of(App.status), equals('Ready!')),
 *          // app is ready, proceed with the scenario
 *      );
 *
 * @see {@link BrowseTheWeb}
 * @see {@link Target}
 * @see {@link Text}
 * @see {@link @serenity-js/assertions~Ensure}
 * @see {@link @serenity-js/assertions/lib/expectations~equals}
 * @see {@link @serenity-js/core/lib/model~Duration}
 *
 * @extends {@serenity-js/core/lib/screenplay~Interaction}
 */
export class Wait {

    /**
     * @desc
     *  Default timeout of 5 seconds used with {@link Wait.until}.
     *
     * @type {@serenity-js/core~Duration}
     */
    static readonly Default_Timeout = Duration.ofSeconds(5);

    /**
     * @desc
     *  Instantiates a version of this {@link @serenity-js/core/lib/screenplay~Interaction}
     *  configured to wait for a set duration.
     *
     * @param {Answerable<Duration>} duration
     *  A set duration the {@link @serenity-js/core/lib/screenplay/actor~Actor} should wait for
     *  before proceeding
     *
     * @returns {@serenity-js/core/lib/screenplay~Interaction}
     */
    static for(duration: Answerable<Duration>): Interaction {
        return new WaitFor(duration);
    }

    /**
     * @desc
     *  Instantiates a version of this {@link @serenity-js/core/lib/screenplay~Interaction}
     *  configured to wait until the answer to the question (`actual`) meets the `expectation`,
     *  or a custom timeout expires.
     *
     * @param {Duration} duration
     *  Custom timeout to override {@link Wait.Default_Timeout}
     *
     * @returns {WaitBuilder}
     */
    static upTo(duration: Duration): WaitBuilder {
        return {
            until: <Actual>(actual: Answerable<Actual>, expectation: Expectation<any, Actual>): Interaction =>
                new WaitUntil(actual, expectation, duration),
        };
    }

    /**
     * @desc
     *  Instantiates a version of this {@link @serenity-js/core/lib/screenplay~Interaction}
     *  configured to wait until the answer to the question (`actual`) meets the `expectation`,
     *  or a {@link Wait.Default_Timeout} expires.
     *
     * @param {Answerable<Actual>} actual
     *  A {@link @serenity-js/core/lib/screenplay~Question}
     *  that the {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  will keep asking until the answer meets
     *  the {@link @serenity-js/core/lib/screenplay/questions~Expectation} provided
     *
     * @param {@serenity-js/core/lib/screenplay/questions~<any,Actual>} expectation
     *  An {@link @serenity-js/core/lib/screenplay/questions~Expectation} to be met before proceeding
     *
     * @returns {@serenity-js/core/lib/screenplay~Interaction}
     */
    static until<Actual>(actual: Answerable<Actual>, expectation: Expectation<any, Actual>): Interaction {
        return new WaitUntil(actual, expectation, Wait.Default_Timeout);
    }
}

/**
 * @package
 */
class WaitFor extends Interaction {
    constructor(private readonly duration: Answerable<Duration>) {
        super();
    }

    /**
     * @desc
     *  Makes the provided {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  perform this {@link @serenity-js/core/lib/screenplay~Interaction}.
     *
     * @param {UsesAbilities & AnswersQuestions} actor
     * @returns {Promise<void>}
     *
     * @see {@link @serenity-js/core/lib/screenplay/actor~Actor}
     * @see {@link @serenity-js/core/lib/screenplay/actor~UsesAbilities}
     * @see {@link @serenity-js/core/lib/screenplay/actor~AnswersQuestions}
     */
    performAs(actor: UsesAbilities & AnswersQuestions): PromiseLike<void> {
        return actor.answer(this.duration)
            .then(duration => BrowseTheWeb.as(actor).sleep(duration.inMilliseconds()));
    }

    /**
     * @desc
     *  Generates a description to be used when reporting this {@link @serenity-js/core/lib/screenplay~Activity}.
     *
     * @returns {string}
     */
    toString(): string {
        return formatted`#actor waits for ${ this.duration }`;
    }
}

/**
 * @package
 */
class WaitUntil<Actual> extends Interaction {
    constructor(
        private readonly actual: Answerable<Actual>,
        private readonly expectation: Expectation<any, Actual>,
        private readonly timeout: Duration,
    ) {
        super();
    }

    /**
     * @desc
     *  Makes the provided {@link @serenity-js/core/lib/screenplay/actor~Actor}
     *  perform this {@link @serenity-js/core/lib/screenplay~Interaction}.
     *
     * @param {UsesAbilities & AnswersQuestions} actor
     * @returns {Promise<void>}
     *
     * @see {@link @serenity-js/core/lib/screenplay/actor~Actor}
     * @see {@link @serenity-js/core/lib/screenplay/actor~UsesAbilities}
     * @see {@link @serenity-js/core/lib/screenplay/actor~AnswersQuestions}
     */
    performAs(actor: UsesAbilities & AnswersQuestions): PromiseLike<void> {
        const
            actual      = this.actual,
            expectation = this.expectation.answeredBy(actor);

        let expectationOutcome: ExpectationOutcome<any, Actual>;

        return BrowseTheWeb.as(actor)
            .wait(function () {
                return actor.answer(actual)
                    .then(act => expectation(act))
                    .then(outcome => {
                        expectationOutcome = outcome;

                        return outcome instanceof ExpectationMet;
                    });
            }, this.timeout.inMilliseconds())
            .then(_ => void 0)
            .catch(error => {
                if (expectationOutcome) {
                    throw new AssertionError(
                        `Waited ${ this.timeout.toString() } for ${ formatted `${ this.actual }` } to ${ this.expectation.toString() }`,
                        expectationOutcome.expected,
                        expectationOutcome.actual,
                        error,
                    );
                }

                throw error;
            });
    }

    /**
     * @desc
     *  Generates a description to be used when reporting this {@link @serenity-js/core/lib/screenplay~Activity}.
     *
     * @returns {string}
     */
    toString(): string {
        return formatted`#actor waits up to ${ this.timeout } until ${ this.actual } does ${ this.expectation }`;
    }
}

