        Navigate.to('/screenplay/interactions/right-click/example.html'),

        Ensure.that(Text.of(Form.Result), equals('')),
        RightClick.on(Form.InputField),
        Ensure.that(Text.of(Form.Result), equals('Test for right click.')),
    ));

    /** @test {RightClick#toString} */
    it('provides a sensible description of the interaction being performed', () => {
        expect(RightClick.on(Form.InputField).toString())
            .to.equal('#actor right clicks on the input field');
    });
});

import 'mocha';

import { expect } from '@integration/testing-tools';
import { Ensure, isGreaterThan, isLessThan } from '@serenity-js/assertions';
import { actorCalled, engage } from '@serenity-js/core';
import { by } from 'protractor';

import { ExecuteScript, LastScriptExecution, Navigate, Scroll, Target } from '../../../src';
import { pageFromTemplate } from '../../fixtures';
import { UIActors } from '../../UIActors';

/** @test {Scroll} */
describe('Scroll', function () {

    const aLongSpell = pageFromTemplate(`
        <html>
            <body style="margin:0; padding:0 0 1024px 0;">
                <input type="submit" value="Cast!" id="cast" style="margin-top:10000px;" />
            </body>
        </html>
    `);

    const Page = {
        Execute_Button: Target.the('"Cast!" button').located(by.id('cast')),
    };

    beforeEach(() => engage(new UIActors()));

    /** @test {Scroll.to} */
    it('allows the actor to scroll to a given target so that it appears in the viewport', () => actorCalled('Gandalf').attemptsTo(
        Navigate.to(aLongSpell),

        ExecuteScript.sync(`return arguments[0].getBoundingClientRect().top;`).withArguments(Page.Execute_Button),
        Ensure.that(LastScriptExecution.result<number>(), isGreaterThan(9000)),

        Scroll.to(Page.Execute_Button),

        ExecuteScript.sync(`return arguments[0].getBoundingClientRect().top;`).withArguments(Page.Execute_Button),
        Ensure.that(LastScriptExecution.result<number>(), isLessThan(9000)),
    ));

    /** @test {Scroll.to} */
    /** @test {Scroll#toString} */
    it('provides a sensible description of the interaction being performed', () => {
        expect(Scroll.to(Page.Execute_Button).toString())
            .to.equal(`#actor scrolls to the "Cast!" button`);
    });
});

import 'mocha';

import { expect } from '@integration/testing-tools';
import { Ensure, equals } from '@serenity-js/assertions';
import { actorCalled, engage } from '@serenity-js/core';
import { LocalServer, StartLocalServer, StopLocalServer } from '@serenity-js/local-server';
import { ChangeApiConfig } from '@serenity-js/rest';
import { by } from 'protractor';

import { Select, Selected, Target, Text } from '../../../src';
import { CreatePage, DeletePage, VisitPage } from '../../pages';
import { UIActors } from '../../UIActors';

/** @test {Select} */
describe('Select', () => {

    const pageWithSingleSelect = `
            <html>
                <body>
                <form>
                    <fieldset name='options'>
                        <legend>Working with single option</legend>
                        <label for='single-option-select'>
                            Country
                            <select id='single-option-select'>
                                <option value='UK'>United Kingdom</option>
                                <option value='PL'>Poland</option>
                                <option value='DE'>Germany</option>
                                <option value='FR'>France</option>
                            </select>
                        </label>
                    </fieldset>
                    <p id='country-of-interest-code'>PL</p>
                    <p id='country-of-interest-name'>Poland</p>
                </form>
                </body>
            </html>
        `;

    class SingleSelectPage {
        static pageName = 'single-select';
        static selector = Target.the('country selector').located(by.id('single-option-select'));
        static countryCode = Target.the('country code').located(by.id('country-of-interest-code'));
        static countryName = Target.the('country name').located(by.id('country-of-interest-name'));
    }

    const pageWithMultiSelect = `
            <html>
                <body>
                <form>
                    <fieldset name='options'>
                        <legend>Working with options</legend>
                        <label for='multi-option-select'>
                            Country
                            <select multiple id='multi-option-select'>
                                <option value='UK'>United Kingdom</option>
                                <option value='PL'>Poland</option>
                                <option value='DE'>Germany</option>
                                <option value='FR'>France</option>
                            </select>
                        </label>
                    </fieldset>
                </form>
                <ul id='country-of-interest-codes'>
                    <li>PL</li>
                    <li>UK</li>
                </ul>
                <ul id='country-of-interest-names'>
                    <li>Poland</li>
                    <li>United Kingdom</li>
                </ul>
                <p id='another-country-of-interest-code'>DE</p>
                <p id='another-country-of-interest-name'>Germany</p>
                </body>
            </html>
        `;

    class MultiSelectPage {
        static pageName = 'multi-select';
        static selector = Target.the('country selector').located(by.id('multi-option-select'));
        static countryCodes = Target.all('country codes').located(by.css('#country-of-interest-codes li'));
        static countryNames = Target.all('country names').located(by.css('#country-of-interest-names li'));
        static anotherCountryCode = Target.the('another country code').located(by.css('#another-country-of-interest-code'));
        static anotherCountryName = Target.the('another country name').located(by.css('#another-country-of-interest-name'));
    }

    before(() => engage(new UIActors()));

    before(() =>
        actorCalled('Nick').attemptsTo(
            StartLocalServer.onRandomPort(),
            ChangeApiConfig.setUrlTo(LocalServer.url()),
            CreatePage(SingleSelectPage.pageName, pageWithSingleSelect),
            CreatePage(MultiSelectPage.pageName, pageWithMultiSelect),
        )
    );

    after(() =>
        actorCalled('Nick').attemptsTo(
            DeletePage(SingleSelectPage.pageName),
            DeletePage(MultiSelectPage.pageName),
            StopLocalServer.ifRunning(),
        )
    )

    describe('when working with single-option selects', () => {

        describe('Select.value()', () => {

            /** @test {Select.value} */
            /** @test {Selected.valueOf} */
            it('should select a single option by its static value', () =>
                actorCalled('Nick').attemptsTo(
                    VisitPage(SingleSelectPage.pageName),
                    Select.value('FR').from(SingleSelectPage.selector),
                    Ensure.that(Selected.valueOf(SingleSelectPage.selector), equals('FR'))
                ));

            /** @test {Select.value} */
            /** @test {Selected.valueOf} */
            it('should select a single option by its Answerable value', () =>
                actorCalled('Nick').attemptsTo(
                    VisitPage(SingleSelectPage.pageName),
                    Select.value(Text.of(SingleSelectPage.countryCode)).from(SingleSelectPage.selector),
                    Ensure.that(Selected.valueOf(SingleSelectPage.selector), equals('PL'))
                ));
        });

        describe('Select.option()', () => {

            /** @test {Select.option} */
            /** @test {Selected.optionIn} */
            it('should select a single option by its static pageName', () =>
                actorCalled('Nick').attemptsTo(
                    VisitPage(SingleSelectPage.pageName),
                    Select.option('France').from(SingleSelectPage.selector),
                    Ensure.that(Selected.optionIn(SingleSelectPage.selector), equals('France'))
                ));

            /** @test {Select.option} */
            /** @test {Selected.optionIn} */
            it('should select a single option by its Answerable name', () =>
                actorCalled('Nick').attemptsTo(
                    VisitPage(SingleSelectPage.pageName),
                    Select.option(Text.of(SingleSelectPage.countryName)).from(SingleSelectPage.selector),
                    Ensure.that(Selected.optionIn(SingleSelectPage.selector), equals('Poland'))
                ));
        });
    });

    describe('when working with multi-option selects', () => {

        describe('Select.values()', () => {

            /** @test {Select.values} */
            /** @test {Selected.valuesOf} */
            it('should select multiple options by their static value', () =>
                actorCalled('Nick').attemptsTo(
                    VisitPage(MultiSelectPage.pageName),
                    Select.values('PL', 'DE').from(MultiSelectPage.selector),
                    Ensure.that(Selected.valuesOf(MultiSelectPage.selector), equals(['PL', 'DE']))
                ));

            /** @test {Select.values} */
            /** @test {Selected.valuesOf} */
            it('should select multiple options by their Answerable value', () =>
                actorCalled('Nick').attemptsTo(
                    VisitPage(MultiSelectPage.pageName),
                    Select.values(Text.ofAll(MultiSelectPage.countryCodes)).from(MultiSelectPage.selector),
                    Ensure.that(Selected.valuesOf(MultiSelectPage.selector), equals(['UK', 'PL']))
                ));

            /** @test {Select.values} */
            /** @test {Selected.valuesOf} */
            it('should concatenate option values from several Answerables', () =>
                actorCalled('Nick').attemptsTo(
                    VisitPage(MultiSelectPage.pageName),
                    Select.values(
                        Text.ofAll(MultiSelectPage.countryCodes),
                        Text.of(MultiSelectPage.anotherCountryCode),
                        'FR'
                    ).from(MultiSelectPage.selector),
                    Ensure.that(Selected.valuesOf(MultiSelectPage.selector), equals(['UK', 'PL', 'DE', 'FR']))
                ));

            /** @test {Select.values} */
            /** @test {Selected.valuesOf} */
            it('should concatenate option values from several static values', () =>
                actorCalled('Nick').attemptsTo(
                    VisitPage(MultiSelectPage.pageName),
                    Select.values('UK', 'PL').from(MultiSelectPage.selector),
                    Ensure.that(Selected.valuesOf(MultiSelectPage.selector), equals(['UK', 'PL']))
                ));
        });

        describe('Select.options()', () => {

            /** @test {Select.options} */
            /** @test {Selected.optionsIn} */
            it('should select multiple options by their static pageName', () =>
                actorCalled('Nick').attemptsTo(
                    VisitPage(MultiSelectPage.pageName),
                    Select.options(['Poland', 'France']).from(MultiSelectPage.selector),
                    Ensure.that(Selected.optionsIn(MultiSelectPage.selector), equals(['Poland', 'France']))));

            /** @test {Select.options} */
            /** @test {Selected.optionsIn} */
            it('should select multiple options by their Answerable name', () =>
                actorCalled('Nick').attemptsTo(
                    VisitPage(MultiSelectPage.pageName),
                    Select.options(Text.ofAll(MultiSelectPage.countryNames)).from(MultiSelectPage.selector),
                    Ensure.that(Selected.optionsIn(MultiSelectPage.selector), equals(['United Kingdom', 'Poland']))
                ));

            /** @test {Select.options} */
            /** @test {Selected.optionsIn} */
            it('should concatenate option values from several Answerables', () =>
                actorCalled('Nick').attemptsTo(
                    VisitPage(MultiSelectPage.pageName),
                    Select.options(
                        Text.ofAll(MultiSelectPage.countryNames),
                        Text.of(MultiSelectPage.anotherCountryName),
                        'France'
                    ).from(MultiSelectPage.selector),
                    Ensure.that(Selected.optionsIn(MultiSelectPage.selector), equals(['United Kingdom', 'Poland', 'Germany', 'France']))
                ));

            /** @test {Select.options} */
            /** @test {Selected.optionsIn} */
            it('should concatenate option values from several static values', () =>
                actorCalled('Nick').attemptsTo(
                    VisitPage(MultiSelectPage.pageName),
                    Select.options(['Poland', 'Germany'], 'France').from(MultiSelectPage.selector),
                    Ensure.that(Selected.optionsIn(MultiSelectPage.selector), equals(['Poland', 'Germany', 'France']))
                ));
        });
    });

    describe('toString()', () => {

        /** @test {Select.value} */
        /** @test {Select#toString} */
        it('provides a sensible description of Select.value()', () => {
            expect(Select.value('FR').from(SingleSelectPage.selector).toString())
                .to.equal(`#actor selects value 'FR' from the country selector`);
        });

        /** @test {Selected.valueOf} */
        /** @test {Select#toString} */
        it('provides a sensible description of Selected.valueOf', () => {
            expect(Selected.valueOf(SingleSelectPage.selector).toString())
                .to.equal(`value selected in the country selector`);
        });

        /** @test {Select.option} */
        /** @test {Select#toString} */
        it('provides a sensible description of Select.option()', () => {
            expect(Select.option('France').from(SingleSelectPage.selector).toString())
                .to.equal(`#actor selects 'France' from the country selector`);
        });

        /** @test {Selected.optionIn} */
        /** @test {Select#toString} */
        it('provides a sensible description of Selected.optionIn()', () => {
            expect(Selected.optionIn(SingleSelectPage.selector).toString())
                .to.equal(`option selected in the country selector`);
        });

        /** @test {Select.values} */
        /** @test {Select#toString} */
        it('provides a sensible description of Select.values()', () => {
            expect(Select.values(['PL', 'DE'], 'FR').from(MultiSelectPage.selector).toString())
                .to.equal(`#actor selects values 'PL', 'DE' and 'FR' from the country selector`);
        });

        /** @test {Selected.valuesOf} */
        /** @test {Select#toString} */
        it('provides a sensible description of Select.valuesOf()', () => {
            expect(Selected.valuesOf(MultiSelectPage.selector).toString())
                .to.equal(`values selected in the country selector`);
        });

        /** @test {Select.options} */
        /** @test {Select#toString} */
        it('provides a sensible description of Select.options()', () => {
            expect(
                Select.options(
                    ['Poland', 'Germany' ],
                    'France',
                    Text.of(MultiSelectPage.anotherCountryName)
                ).from(MultiSelectPage.selector).toString()
            ).to.equal(`#actor selects 'Poland', 'Germany', 'France' and the text of the another country name from the country selector`);
        });

        /** @test {Selected.optionsIn} */
        /** @test {Select#toString} */
        it('provides a sensible description of Selected.optionsIn()', () => {
            expect(Selected.optionsIn(MultiSelectPage.selector).toString())
                .to.equal(`options selected in the country selector`);
        });
    });
});

/* eslint-disable unicorn/consistent-function-scoping */
import 'mocha';

import { expect } from '@integration/testing-tools';
import { Ensure, equals } from '@serenity-js/assertions';
import { actorCalled, engage, LogicError } from '@serenity-js/core';
import { by } from 'protractor';
import { error } from 'selenium-webdriver';

import { Click, Close, Navigate, Switch, Target, Text } from '../../../src';
import { pageFromTemplate } from '../../fixtures';
import { UIActors } from '../../UIActors';

/** @test {Switch} */
describe('Switch', () => {

    const
        h1 = Target.the('header').located(by.css('h1')),
        iframe = Target.the('test iframe').located(by.tagName('iframe')),
        newTabLink = Target.the('link').located(by.linkText('open new tab'));

    const page =
        (header: string) =>
            pageFromTemplate(`
                <html>
                    <body>
                        <h1>${ header }</h1>
                    </body>
                </html>
            `);

    const pageWithIframe =
        (header: string, iframeSource: string) =>
            pageFromTemplate(`
                <html>
                    <body>
                        <h1>${ header }</h1>
                        <iframe name="example-iframe" src="${ iframeSource.replace(/"/g, '&quot;') }" />
                    </body>
                </html>
            `);

    const pageWithLinkToNewTab =
        (header: string) =>
            pageFromTemplate(`
                <html>
                    <body>
                        <h1>${ header }</h1>
                        <a href="javascript:void(0)" onclick="popup()">open new tab</a>
                        <script>
                            function popup() {
                                var w = window.open('', 'new-tab');
                                w.document.write('<h1>New tab</h1>');
                                w.document.close();
                            }
                        </script>
                    </body>
                </html>
            `);

    beforeEach(() => engage(new UIActors()));

    describe('when working with frames', () => {

        describe('toFrame()', () => {

            /** @test {Switch.toFrame} */
            it(`should complain if there's no frame to switch to`, () =>
                expect(actorCalled('Franceska').attemptsTo(
                    Navigate.to(page('main page')),
                    Switch.toFrame(0),
                )).to.be.rejectedWith(error.NoSuchFrameError));

            /** @test {Switch.toFrame} */
            it('should switch to an iframe identified by Question<ElementFinder>', () =>
                actorCalled('Franceska').attemptsTo(
                    Navigate.to(pageWithIframe('main page', page('an iframe'))),
                    Ensure.that(Text.of(h1), equals('main page')),

                    Switch.toFrame(iframe),
                    Ensure.that(Text.of(h1), equals('an iframe')),
                ));

            /** @test {Switch.toFrame} */
            it('should switch to an iframe identified by index', () =>
                actorCalled('Franceska').attemptsTo(
                    Navigate.to(pageWithIframe('main page', page('an iframe'))),
                    Ensure.that(Text.of(h1), equals('main page')),

                    Switch.toFrame(0),
                    Ensure.that(Text.of(h1), equals('an iframe')),
                ));

            /** @test {Switch.toFrame} */
            it('should switch to an iframe identified by name', () =>
                actorCalled('Franceska').attemptsTo(
                    Navigate.to(pageWithIframe('main page', page('an iframe'))),
                    Ensure.that(Text.of(h1), equals('main page')),

                    Switch.toFrame('example-iframe'),
                    Ensure.that(Text.of(h1), equals('an iframe')),
                ));

            describe('should provide a sensible description when the iframe', () => {

                /** @test {Switch.toFrame} */
                /** @test {Switch#toString} */
                it('is specified by Question<ElementFinder>', () => {
                    expect(Switch.toFrame(iframe).toString()).to.equal('#actor switches to frame: the test iframe')
                });

                /** @test {Switch.toFrame} */
                /** @test {Switch#toString} */
                it('is specified by index number', () => {
                    expect(Switch.toFrame(1).toString()).to.equal('#actor switches to frame: 1')
                });

                /** @test {Switch.toFrame} */
                /** @test {Switch#toString} */
                it('is specified by name', () => {
                    expect(Switch.toFrame('example-iframe').toString()).to.equal('#actor switches to frame: example-iframe')
                });
            });
        });

        describe('toParentFrame()', () => {

            /** @test {Switch.toParentFrame} */
            it('should not switch the frame if there are no parent frames', () =>
                actorCalled('Franceska').attemptsTo(
                    Navigate.to(page('main page')),
                    Ensure.that(Text.of(h1), equals('main page')),

                    Switch.toParentFrame(),
                    Ensure.that(Text.of(h1), equals('main page')),
                ));

            /** @test {Switch.toParentFrame} */
            it('should switch to the parent hosting an iframe', () =>
                actorCalled('Franceska').attemptsTo(
                    Navigate.to(pageWithIframe('main page', page('an iframe'))),
                    Ensure.that(Text.of(h1), equals('main page')),

                    Switch.toFrame(iframe),
                    Ensure.that(Text.of(h1), equals('an iframe')),

                    Switch.toParentFrame(),
                    Ensure.that(Text.of(h1), equals('main page')),
                ));

            /** @test {Switch.toFrame} */
            /** @test {Switch.toParentFrame} */
            it('should support nested frames', () =>
                actorCalled('Franceska').attemptsTo(
                    Navigate.to(pageWithIframe('Level 0', pageWithIframe('Level 1', page('Level 2')))),
                    Ensure.that(Text.of(h1), equals('Level 0')),

                    Switch.toFrame(iframe),
                    Ensure.that(Text.of(h1), equals('Level 1')),

                    Switch.toFrame(iframe),
                    Ensure.that(Text.of(h1), equals('Level 2')),

                    Switch.toParentFrame(),
                    Ensure.that(Text.of(h1), equals('Level 1')),
                ));

            /** @test {Switch.toParentFrame} */
            /** @test {Switch#toString} */
            it('should provide a sensible description of the interaction being performed', () => {
                expect(Switch.toParentFrame().toString()).to.equal('#actor switches to parent frame')
            });
        });

        describe('toDefaultContent()', () => {

            /** @test {Switch.toDefaultContent} */
            it('should not switch the frame if there are no parent frames', () =>
                actorCalled('Franceska').attemptsTo(
                    Navigate.to(page('main page')),
                    Ensure.that(Text.of(h1), equals('main page')),

                    Switch.toDefaultContent(),
                    Ensure.that(Text.of(h1), equals('main page')),
                ));

            /** @test {Switch.toFrame} */
            /** @test {Switch.toDefaultContent} */
            it('should support nested frames', () =>
                actorCalled('Franceska').attemptsTo(
                    Navigate.to(pageWithIframe('Level 0', pageWithIframe('Level 1', page('Level 2')))),
                    Ensure.that(Text.of(h1), equals('Level 0')),

                    Switch.toFrame(iframe),
                    Ensure.that(Text.of(h1), equals('Level 1')),

                    Switch.toFrame(iframe),
                    Ensure.that(Text.of(h1), equals('Level 2')),

                    Switch.toDefaultContent(),
                    Ensure.that(Text.of(h1), equals('Level 0')),
                ));

            /** @test {Switch.toDefaultContent} */
            /** @test {Switch#toString} */
            it('should provide a sensible description of the interaction being performed', () => {
                expect(Switch.toDefaultContent().toString()).to.equal('#actor switches to default content')
            });
        });

        describe('toFrame().and()', () => {

            /** @test {Switch.toFrame} */
            it('should perform any activities in the context of the frame it switched to', () =>
                actorCalled('Franceska').attemptsTo(
                    Navigate.to(pageWithIframe('Level 0', pageWithIframe('Level 1', page('Level 2')))),
                    Ensure.that(Text.of(h1), equals('Level 0')),

                    Switch.toFrame(iframe).and(
                        Switch.toFrame(iframe).and(
                            Ensure.that(Text.of(h1), equals('Level 2')),
                        ),
                        Ensure.that(Text.of(h1), equals('Level 1')),
                    ),
                    Ensure.that(Text.of(h1), equals('Level 0')),
                ));

            /** @test {Switch.toFrame} */
            /** @test {Switch#toString} */
            it('should provide a sensible description of the interaction being performed', () => {
                expect(Switch.toFrame(0).and().toString()).to.equal('#actor switches to frame: 0')
            });
        });
    });

    describe('when working with windows', () => {

        afterEach(() =>
            actorCalled('Ventana').attemptsTo(
                Close.anyNewWindows(),
            ));

        describe('toWindow()', () => {

            /** @test {Switch.toWindow} */
            it('should switch to a window identified by its index', () =>
                actorCalled('Ventana').attemptsTo(
                    Navigate.to(pageWithLinkToNewTab('Main window')),
                    Ensure.that(Text.of(h1), equals('Main window')),
                    Click.on(newTabLink),

                    Switch.toWindow(1),
                    Ensure.that(Text.of(h1), equals('New tab')),
                ));

            /** @test {Switch.toWindow} */
            it('should switch to a window identified by its name', () =>
                actorCalled('Ventana').attemptsTo(
                    Navigate.to(pageWithLinkToNewTab('Main window')),
                    Ensure.that(Text.of(h1), equals('Main window')),
                    Click.on(newTabLink),

                    Switch.toWindow('new-tab'),
                    Ensure.that(Text.of(h1), equals('New tab')),
                ));

            /** @test {Switch.toWindow} */
            it(`should complain if the desired window doesn't exit`, () =>
                expect(actorCalled('Ventana').attemptsTo(
                    Navigate.to(page('Main window')),

                    Switch.toWindow(10),
                )).to.be.rejectedWith(LogicError, `Window 10 doesn't exist`));

            describe('should provide a sensible description when the window', () => {

                /** @test {Switch.toWindow} */
                /** @test {Switch#toString} */
                it('is specified by its index', () => {
                    expect(Switch.toWindow(1).toString()).to.equal('#actor switches to window: 1')
                });

                /** @test {Switch.toWindow} */
                /** @test {Switch#toString} */
                it('is specified by name', () => {
                    expect(Switch.toWindow('example-window').toString()).to.equal('#actor switches to window: example-window')
                });
            });
        });

        describe('toWindow().and()', () => {

            /** @test {Switch.toWindow} */
            it('should perform any activities in the context of the window it switched to', () =>
                actorCalled('Ventana').attemptsTo(
                    Navigate.to(pageWithLinkToNewTab('Main window')),
                    Ensure.that(Text.of(h1), equals('Main window')),
                    Click.on(newTabLink),

                    Switch.toWindow('new-tab').and(
                        Ensure.that(Text.of(h1), equals('New tab')),
                    ),
                    Ensure.that(Text.of(h1), equals('Main window')),
                ));

            /** @test {Switch.toWindow} */
            /** @test {Switch#toString} */
            it('should provide a sensible description of the interaction being performed', () => {
                expect(Switch.toWindow('new-tab').and().toString()).to.equal('#actor switches to window: new-tab')
            });
        });

        describe(`toNewWindow()`, () => {

            /** @test {Switch.toNewWindow} */
            it(`should complain if no new window has been opened`, () =>
                expect(actorCalled('Ventana').attemptsTo(
                    Navigate.to(page('Main window')),

                    Switch.toNewWindow(),
                )).to.be.rejectedWith(LogicError, `No new window has been opened to switch to`));

            /** @test {Switch.toNewWindow} */
            it('should switch to a newly opened window', () =>
                actorCalled('Ventana').attemptsTo(
                    Navigate.to(pageWithLinkToNewTab('Main window')),
                    Ensure.that(Text.of(h1), equals('Main window')),
                    Click.on(newTabLink),

                    Switch.toNewWindow(),
                    Ensure.that(Text.of(h1), equals('New tab')),
                ));

            /** @test {Switch.toNewWindow} */
            /** @test {Switch#toString} */
            it('should provide a sensible description of the interaction being performed', () => {
                expect(Switch.toNewWindow().toString()).to.equal('#actor switches to the new browser window')
            });
        });

        describe('toNewWindow().and()', () => {

            /** @test {Switch.toNewWindow} */
            it('should perform any activities in the context of the new window it switched to', () =>
