import 'mocha';

import { certificates, expect } from '@integration/testing-tools';
import { Ensure, equals, isTrue } from '@serenity-js/assertions';
import { actorCalled, engage, Question, Transform } from '@serenity-js/core';
import { LocalServer, ManageALocalServer, StartLocalServer, StopLocalServer } from '@serenity-js/local-server';
import express = require('express');

import { protractor } from 'protractor';

import { BrowseTheWeb, DeleteCookies, Navigate } from '../../../src';
import { Cookie } from '../../../src/screenplay';
import { UIActors } from '../../UIActors';

describe('Cookie', () => {

    // a tiny express server, setting response cookies
    const cookieCutterApp = express().
        get('/cookie', (request: express.Request & { query: { [key: string]: string }}, response: express.Response) => {
            response.cookie(request.query.name, request.query.value, {
                path:       '/cookie',
                domain:     request.query.domain,
                httpOnly:   !! request.query.httpOnly,
                secure:     !! request.query.secure,
                expires:    request.query.expires && new Date(request.query.expires),
                // https://www.chromestatus.com/feature/5633521622188032
                // sameSite:   !! request.query.secure ? 'None' : undefined,
            }).status(200).send();
        });

    function cookieCutterURLFor(path: string): Question<Promise<string>> {
        return LocalServer.url().map(actor => url => `${ url }${ path }`);
    }

    beforeEach(() => engage(new UIActors()));

    describe('over HTTP', () => {

        // Fun fact: Before Cookie Monster ate his first cookie, he believed his name was Sid. You're welcome.
        const Sid = actorCalled('Sid').whoCan(
            BrowseTheWeb.using(protractor.browser),
            ManageALocalServer.runningAHttpListener(cookieCutterApp),
        );

        beforeEach(() => Sid.attemptsTo(StartLocalServer.onRandomPort()));
        afterEach(() => Sid.attemptsTo(StopLocalServer.ifRunning()));
        afterEach(() => Sid.attemptsTo(DeleteCookies.all()));

        describe('when working with the value', () => {

            /** @test {Cookie} */
            /** @test {Cookie#valueOf} */
            it('allows the actor to retrieve it', () => Sid.attemptsTo(
                Navigate.to(cookieCutterURLFor('/cookie?name=favourite&value=chocolate-chip')),
                Ensure.that(Cookie.valueOf('favourite'), equals('chocolate-chip')),
            ));

            /** @test {Cookie} */
            /** @test {Cookie#valueOf} */
            it('returns an undefined when it can\'t retrieve it', () => Sid.attemptsTo(
                Navigate.to(cookieCutterURLFor('/cookie?name=favourite&value=chocolate-chip')),
                Ensure.that(Cookie.valueOf('not-so-favourite'), equals(undefined)),
            ));

            /** @test {Cookie} */
            it('provides a sensible description of the question being asked', () => {
                expect(Cookie.valueOf('favourite').toString()).to.equal('the value of the "favourite" cookie');
            });
        });

        describe('when working with the path', () => {

            /** @test {Cookie} */
            /** @test {Cookie#valueOf} */
            it('allows the actor to retrieve it', () => Sid.attemptsTo(
                Navigate.to(cookieCutterURLFor('/cookie?name=favourite&value=chocolate-chip')),
                Ensure.that(Cookie.pathOf('favourite'), equals('/cookie')),
            ));

            /** @test {Cookie} */
            /** @test {Cookie#pathOf} */
            it('returns an undefined when it can\'t retrieve it', () => Sid.attemptsTo(
                Navigate.to(cookieCutterURLFor('/cookie?name=favourite&value=chocolate-chip')),
                Ensure.that(Cookie.pathOf('not-so-favourite'), equals(undefined)),
            ));

            /** @test {Cookie} */
            it('provides a sensible description of the question being asked', () => {
                expect(Cookie.pathOf('favourite').toString()).to.equal('the path of the "favourite" cookie');
            });
        });

        describe('when working with the domain', () => {

            /** @test {Cookie} */
            /** @test {Cookie#valueOf} */
            it('allows the actor to retrieve it', () => Sid.attemptsTo(
                Navigate.to(cookieCutterURLFor('/cookie?name=favourite&value=chocolate-chip')),
                Ensure.that(Cookie.domainOf('favourite'), equals('127.0.0.1')),
            ));

            /** @test {Cookie} */
            /** @test {Cookie#domainOf} */
            it('returns an undefined when it can\'t retrieve it', () => Sid.attemptsTo(
                Navigate.to(cookieCutterURLFor('/cookie?name=favourite&value=chocolate-chip')),
                Ensure.that(Cookie.domainOf('not-so-favourite'), equals(undefined)),
            ));

            /** @test {Cookie} */
            it('provides a sensible description of the question being asked', () => {
                expect(Cookie.domainOf('favourite').toString()).to.equal('the domain of the "favourite" cookie');
            });
        });

        describe('when working with http-only cookies', () => {

            /** @test {Cookie} */
            /** @test {Cookie#isHTTPOnly} */
            it('allows the actor to confirm that a cookie is http-only', () => Sid.attemptsTo(
                Navigate.to(cookieCutterURLFor('/cookie?name=favourite&value=chocolate-chip')),
                Ensure.that(Cookie.isHTTPOnly('favourite'), equals(false)),

                Navigate.to(cookieCutterURLFor('/cookie?name=second_choice&value=shortbread&httpOnly=true')),
                Ensure.that(Cookie.isHTTPOnly('second_choice'), isTrue()),
            ));

            /** @test {Cookie} */
            /** @test {Cookie#isHTTPOnly} */
            it('returns an undefined when it can\'t retrieve it', () => Sid.attemptsTo(
                Navigate.to(cookieCutterURLFor('/cookie?name=favourite&value=chocolate-chip')),
                Ensure.that(Cookie.isHTTPOnly('not-so-favourite'), equals(undefined)),
            ));

            /** @test {Cookie} */
            it('provides a sensible description of the question being asked', () => {
                expect(Cookie.isHTTPOnly('favourite').toString()).to.equal('the HTTP-only status of the "favourite" cookie');
            });
        });

        describe('when working with an expiry date', () => {

            function tomorrow(): Date {
                const now = new Date();
                const nextDay = new Date(now);
                nextDay.setDate(nextDay.getDate() + 1);

                return nextDay;
            }

            const expectedExpiryDate = tomorrow();

            /** @test {Cookie} */
            /** @test {Cookie#expiryDateOf} */
            it('allows the actor to retrieve it', () => Sid.attemptsTo(
                Navigate.to(cookieCutterURLFor(`/cookie?name=expiring&value=chocolate-chip&expires=${ expectedExpiryDate.toISOString() }`)),
                Ensure.that(Transform.the(Cookie.expiryDateOf('expiring'), date => date.getDay()), equals(expectedExpiryDate.getDay())),
            ));

            /** @test {Cookie} */
            /** @test {Cookie#expiryDateOf} */
            it('returns an undefined when it can\'t retrieve it', () => Sid.attemptsTo(
                Navigate.to(cookieCutterURLFor('/cookie?name=favourite&value=chocolate-chip')),
                Ensure.that(Cookie.expiryDateOf('not-so-favourite'), equals(undefined)),
            ));

            /** @test {Cookie} */
            it('provides a sensible description of the question being asked', () => {
                expect(Cookie.expiryDateOf('favourite').toString()).to.equal('the expiry date of the "favourite" cookie');
            });
        });

    });

    describe('when working with secure cookies', () => {

        const Sid = actorCalled('Secure Sid').whoCan(
            BrowseTheWeb.using(protractor.browser),
            ManageALocalServer.runningAHttpsListener(cookieCutterApp, {
                cert:               certificates.cert,
                key:                certificates.key,
                requestCert:        true,
                rejectUnauthorized: false,
            }),
        );

        beforeEach(() => Sid.attemptsTo(StartLocalServer.onRandomPort()));
        afterEach(() => Sid.attemptsTo(StopLocalServer.ifRunning()));
        afterEach(() => Sid.attemptsTo(DeleteCookies.all()));

        /** @test {Cookie} */
        /** @test {Cookie#isSecure} */
        it('allows the actor to confirm that a cookie is not secure', () => Sid.attemptsTo(
            Navigate.to(cookieCutterURLFor('/cookie?name=favourite&value=chocolate-chip')),
            Ensure.that(Cookie.isSecure('favourite'), equals(false)),
        ));

        /** @test {Cookie} */
        /** @test {Cookie#isSecure} */
        it('allows the actor to confirm that a cookie is secure', () => Sid.attemptsTo(
            Navigate.to(cookieCutterURLFor('/cookie?name=favourite&value=chocolate-chip&secure=1')),
            Ensure.that(Cookie.isSecure('favourite'), isTrue()),
        ));

        /** @test {Cookie} */
        /** @test {Cookie#isSecure} */
        it(`returns an undefined when it can't retrieve it`, () => Sid.attemptsTo(
            Navigate.to(cookieCutterURLFor('/cookie?name=favourite&value=chocolate-chip')),
            Ensure.that(Cookie.isSecure('not-so-favourite'), equals(undefined)),
        ));

        /** @test {Cookie} */
        it('provides a sensible description of the question being asked', () => {
            expect(Cookie.isSecure('favourite').toString()).to.equal('the "secure" status of the "favourite" cookie');
        });
    });
});

import 'mocha';

import { expect } from '@integration/testing-tools';
import { Ensure, equals } from '@serenity-js/assertions';
import { actorCalled, engage, LogicError } from '@serenity-js/core';
import { by } from 'protractor';

import { Enter, ExecuteScript, LastScriptExecution, Navigate, Target } from '../../../src';
import { pageFromTemplate } from '../../fixtures';
import { UIActors } from '../../UIActors';

/** @test {LastScriptExecution} */
describe('LastScriptExecution', function () {

    const page = pageFromTemplate(`
        <html>
            <body>
                <form>
                    <input type="text" id="name" />
                </form>
            </body>
        </html>
    `);

    class Sandbox {
        static Input = Target.the('input field').located(by.id('name'));
    }

    beforeEach(() => engage(new UIActors()));

    describe('when used with ExecuteScript.sync', () => {

        /** @test {ExecuteScript.sync} */
        /** @test {ExecuteSynchronousScript} */
        /** @test {LastScriptExecution} */
        it('allows the actor to retrieve the result of the script execution', () => actorCalled('Joe').attemptsTo(
            Navigate.to(page),

            Enter.theValue(actorCalled('Joe').name).into(Sandbox.Input),

            ExecuteScript.sync(`
                var field = arguments[0];
                return field.value;
            `).withArguments(Sandbox.Input),

            Ensure.that(LastScriptExecution.result<string>(), equals(actorCalled('Joe').name)),
        ));

        /** @test {ExecuteScript.sync} */
        /** @test {ExecuteSynchronousScript} */
        /** @test {LastScriptExecution} */
        it('returns null if the script did not return any result', () => actorCalled('Joe').attemptsTo(
            Navigate.to(page),

            ExecuteScript.sync(`
                /* do nothing */
            `),

            Ensure.that(LastScriptExecution.result<null>(), equals(null)),  // eslint-disable-line unicorn/no-null
        ));
    });

    describe('when used with ExecuteScript.async', () => {

        /** @test {ExecuteScript.async} */
        /** @test {ExecuteAsynchronousScript} */
        /** @test {LastScriptExecution} */
        it('allows the actor to retrieve the result of the script execution', () => actorCalled('Joe').attemptsTo(
            Navigate.to(page),

            Enter.theValue(actorCalled('Joe').name).into(Sandbox.Input),

            ExecuteScript.async(`
                var field = arguments[0];
                var callback = arguments[arguments.length - 1];
                callback(field.value);
            `).withArguments(Sandbox.Input),

            Ensure.that(LastScriptExecution.result<string>(), equals(actorCalled('Joe').name)),
        ));

        /** @test {ExecuteScript.async} */
        /** @test {ExecuteAsynchronousScript} */
        /** @test {LastScriptExecution} */
        it('returns null if the script did not return any result', () => actorCalled('Joe').attemptsTo(
            Navigate.to(page),

            ExecuteScript.async(`
                var callback = arguments[arguments.length - 1];
                callback();
            `),

            Ensure.that(LastScriptExecution.result<null>(), equals(null)),  // eslint-disable-line unicorn/no-null
        ));
    });

    /** @test {ExecuteAsynchronousScript} */
    /** @test {LastScriptExecution} */
    it('complains if the script hasn\'t been executed yet', () => expect(actorCalled('Joe').attemptsTo(
        Navigate.to(page),

        Ensure.that(LastScriptExecution.result<string>(), equals(actorCalled('Joe').name)),
    )).to.be.rejectedWith(LogicError, 'Make sure to execute a script before checking on the result'));
});

import 'mocha';

import { EventRecorder, expect, PickEvent } from '@integration/testing-tools';
import { Ensure, equals, isFalse, isTrue } from '@serenity-js/assertions';
import { actorCalled, configure, engage } from '@serenity-js/core';
import { AsyncOperationCompleted, InteractionFinished } from '@serenity-js/core/lib/events';
import { Name } from '@serenity-js/core/lib/model';
import { by } from 'protractor';

import { Accept, Click, Dismiss, Enter, ModalDialog, Navigate, Photographer, TakePhotosOfInteractions, Target, Text, Wait } from '../../../src';
import { pageFromTemplate } from '../../fixtures';
import { UIActors } from '../../UIActors';

/** @test {ModalDialog} */
describe('ModalDialog,', function () {

    const Example = {
        trigger:    Target.the('alert trigger').located(by.id('trigger')),
        result:     Target.the('result').located(by.id('result')),
    }

    beforeEach(() => engage(new UIActors()));

    describe('when working with alert(),', () => {

        beforeEach(() =>
            actorCalled('Nick').attemptsTo(
                Navigate.to(sandboxWith(`
                    function() {
                        alert('Hello!');
                        // alert is blocking
                        return 'accepted';
                    }
                `)),
                Click.on(Example.trigger),
            ));

        /** @test {ModalDialog} */
        /** @test {ModalDialog.window} */
        /** @test {Accept} */
        /** @test {Accept.the} */
        it('allows the actor to accept an alert', () =>
            actorCalled('Nick').attemptsTo(
                Accept.the(ModalDialog.window()),
                Ensure.that(Text.of(Example.result), equals('accepted')),
            ),
        );

        /** @test {ModalDialog} */
        /** @test {ModalDialog.window} */
        /** @test {Dismiss} */
        /** @test {Dismiss.the} */
        it('allows the actor to dismiss an alert', () =>
            actorCalled('Nick').attemptsTo(
                Dismiss.the(ModalDialog.window()),
                Ensure.that(Text.of(Example.result), equals('accepted')),
            ),
        );

        /** @test {ModalDialog} */
        /** @test {ModalDialog.message} */
        /** @test {Dismiss} */
        /** @test {Dismiss.the} */
        it('allows the actor to read the message on an alert', () =>
            actorCalled('Nick').attemptsTo(
                Ensure.that(ModalDialog.message(), equals('Hello!')),
                Dismiss.the(ModalDialog.window()),
            ),
        );
    });

    describe('when working with confirm(),', () => {

        beforeEach(() =>
            actorCalled('Nick').attemptsTo(
                Navigate.to(sandboxWith(`
                    function() {
                        return confirm('Continue?')
                            ? 'accepted'
                            : 'dismissed';
                    }
                `)),
                Click.on(Example.trigger),
            ));

        /** @test {ModalDialog} */
        /** @test {ModalDialog.window} */
        /** @test {Accept} */
        /** @test {Accept.the} */
        it('allows the actor to accept a confirmation dialog', () =>
            actorCalled('Nick').attemptsTo(
                Accept.the(ModalDialog.window()),
                Ensure.that(Text.of(Example.result), equals('accepted')),
            ),
        );

        /** @test {ModalDialog} */
        /** @test {ModalDialog.window} */
        /** @test {Dismiss} */
        /** @test {Dismiss.the} */
        it('allows the actor to dismiss a confirmation dialog', () =>
            actorCalled('Nick').attemptsTo(
                Dismiss.the(ModalDialog.window()),
                Ensure.that(Text.of(Example.result), equals('dismissed')),
            ),
        );

        /** @test {ModalDialog} */
        /** @test {ModalDialog.message} */
        it('allows the actor to read the message on a confirmation dialog', () =>
            actorCalled('Nick').attemptsTo(
                Ensure.that(ModalDialog.message(), equals('Continue?')),
                Dismiss.the(ModalDialog.window()),
            ),
        );
    });

    describe('when working with prompt(),', () => {

        beforeEach(() =>
            actorCalled('Nick').attemptsTo(
                Navigate.to(sandboxWith(`
                    function() {
                        return prompt('Feeling lucky?', 'sure');
                    }
                `)),
                Click.on(Example.trigger),
            ));

        /** @test {ModalDialog} */
        /** @test {ModalDialog.window} */
        /** @test {Accept} */
        /** @test {Accept.the} */
        it('allows the actor to accept a prompt', () =>
            actorCalled('Nick').attemptsTo(
                Accept.the(ModalDialog.window()),
                Ensure.that(Text.of(Example.result), equals('sure')),
            ),
        );

        /** @test {ModalDialog} */
        /** @test {ModalDialog.window} */
        /** @test {Dismiss} */
        /** @test {Dismiss.the} */
        it('allows the actor to dismiss a prompt', () =>
            actorCalled('Nick').attemptsTo(
                Dismiss.the(ModalDialog.window()),
                Ensure.that(Text.of(Example.result), equals('')),
            ),
        );

        /** @test {ModalDialog} */
        /** @test {ModalDialog.message} */
        it('allows the actor to read the message on a prompt', () =>
            actorCalled('Nick').attemptsTo(
                Ensure.that(ModalDialog.message(), equals('Feeling lucky?')),
                Dismiss.the(ModalDialog.window()),
            ),
        );

        /** @test {ModalDialog} */
        /** @test {ModalDialog.message} */
        /** @test {Enter.theValue} */
        it('allows the actor to enter value into a prompt', () =>
            actorCalled('Nick').attemptsTo(
                Enter.theValue('certainly').into(ModalDialog.window()),
                Accept.the(ModalDialog.window()),
                Ensure.that(Text.of(Example.result), equals('certainly')),
            ),
        );
    });

    describe('when waiting', () => {

        beforeEach(() =>
            actorCalled('Nick').attemptsTo(
                Navigate.to(sandboxWith(`
                    function() {
                        setTimeout(function() {
                            alert('Almost there!');
                            document.getElementById("result").innerHTML = 'And the wait is over :-)';
                        }, 250);
                        return 'The wait has began';
                    }
                `)),
            ));

        /** @test {ModalDialog.hasPoppedUp} */
        /** @test {Wait.until} */
        it('allows the actor to wait until a modal dialog is present', () =>
            actorCalled('Nick').attemptsTo(
                Ensure.that(ModalDialog.hasPoppedUp(), isFalse()),
                Click.on(Example.trigger),
                Wait.until(ModalDialog.hasPoppedUp(), isTrue()),
                Accept.the(ModalDialog.window()),
                Ensure.that(Text.of(Example.result), equals('And the wait is over :-)')),
            ),
        );
    });

    describe('when interacting with the Photographer,', () => {

        let recorder: EventRecorder;

        beforeEach(() => {
            recorder = new EventRecorder();

            configure({
                actors: new UIActors(),
                crew: [
                    Photographer.whoWill(TakePhotosOfInteractions),
                    recorder,
                ]
            })
        });

        /** @test {Photographer} */
        it('is does not negatively impact the screenshot capture process', () =>
            actorCalled('Nick').attemptsTo(
                Navigate.to(sandboxWith(`
                    function() {
                        return alert('All good?');
                    }
                `)),
                Click.on(Example.trigger),
                Accept.the(ModalDialog.window()),
            ).
            then(() => {
                PickEvent.from(recorder.events)
                    .next(AsyncOperationCompleted, ({ taskDescription }: AsyncOperationCompleted) => {
                        expect(taskDescription.value).to.include(`Took screenshot of 'Nick navigates`);
                    })
                    .next(AsyncOperationCompleted, ({ taskDescription }: AsyncOperationCompleted) => {
                        expect(taskDescription.value).to.include(`Aborted taking screenshot of 'Nick clicks on the alert trigger' because of UnexpectedAlertOpenError`);
                    })
                    .next(InteractionFinished, ({ details }: InteractionFinished) => {
                        expect(details.name).to.equal(new Name('Nick accepts the modal dialog window'));
                    })
            }),
        );

    });
});

function sandboxWith(script: string) {
    return pageFromTemplate(`
                <html>
                <body>
                    <button id="trigger" onclick="trigger()">Trigger Alert</button>
                    <p id="result"></p>
                    <script>
                        function trigger() {
                            document.getElementById("result").innerHTML = (${script})();
                        }
                    </script>
                </body>
                </html>
            `)
}

/* eslint-disable unicorn/consistent-function-scoping */
import 'mocha';

import { expect } from '@integration/testing-tools';
import { contain, Ensure, equals, startsWith } from '@serenity-js/assertions';
import { actorCalled, engage, Question } from '@serenity-js/core';
import { given } from 'mocha-testdata';
import { by, ElementArrayFinder, ElementFinder } from 'protractor';

import { Click, CSSClasses, Navigate, Pick, Target, Text } from '../../../src';
import { pageFromTemplate } from '../../fixtures';
import { UIActors } from '../../UIActors';

describe('Pick', () => {

    const shoppingListPage = pageFromTemplate(`
        <html>
        <body>
        <div id="shopping-list-app">
            <h1>Shopping list</h1>
            <ul>
                <li class="buy 1st">
                    <span class="item-name">oats</span>
                    <a onclick="toggle(this)">x</a>
                </li>
                <li class="buy 2nd">
                    <span class="item-name">coconut milk</span>
                    <a onclick="toggle(this)">x</a>
                </li>
                <li class="3rd">
                    <span class="item-name">coffee</span>
                    <a onclick="toggle(this)">x</a>
                </li>
            </ul>
        </div>
        <script>
            function toggle(event) {
                event.parentNode.classList.toggle('buy');
            }
        </script>
        </body>
        </html>
    `);

    class ShoppingList {
        static Items        = Target.all('shopping list items').located(by.css('li'));
        static Item         = Target.the('shopping list item').located(by.css('li'));
        static Titles       = Target.all('shopping list item titles').located(by.css('li span.item-name'));
        static Item_Name    = Target.the('item name').located(by.tagName('span.item-name'));
        static Item_Names   = Target.all('item names').located(by.tagName('span.item-name'));
    }

    beforeEach(() => engage(new UIActors()));

    describe('(when no filters are applied)', () => {

        describe('lets the actor interact with the list of matching elements so that it', () => {

            const picked = Pick.from<ElementFinder, ElementArrayFinder>(ShoppingList.Titles);

            /** @test {Pick} */
            it('gets the number of items', () => actorCalled('Peter').attemptsTo(
                Navigate.to(shoppingListPage),

                Ensure.that(picked.count(), equals(3)),
            ));

            /** @test {Pick} */
            it('picks all the items', () => actorCalled('Peter').attemptsTo(
                Navigate.to(shoppingListPage),

                Ensure.that(Text.ofAll(picked.all()), contain('coconut milk')),
            ));

            /** @test {Pick} */
            it('picks the first item', () => actorCalled('Peter').attemptsTo(
                Navigate.to(shoppingListPage),

                Ensure.that(Text.of(picked.first()), equals('oats')),
            ));

            /** @test {Pick} */
            it('picks the last item', () => actorCalled('Peter').attemptsTo(
                Navigate.to(shoppingListPage),

                Ensure.that(Text.of(picked.last()), equals('coffee')),
            ));

